const int MAX_N = 2000;

bool winL[MAX_N][MAX_N];
bool winR[MAX_N][MAX_N];
bool sumP[MAX_N][MAX_N];


int main() {
  int N;
  cin >> N;
  auto mat = make_v< int >(N, N);
  for(int i = 1; i < N; i++) {
    string s;
    cin >> s;
    for(int j = 0; j < i; j++) {
      int x = (int) s[j] - '0';
      mat[i][j] = x;
      mat[j][i] = 1 - x;
    }
  }

  for(int i = N - 1; i >= 0; i--) {
    winL[i][i] = winR[i][i] = 1;
    for(int j = i + 1; j < N; j++) {
      sumP[i][j] = 0;
      for(int p = i; p < j; p++) {
        sumP[i][j] += winL[i][p] * winR[p + 1][j];
      }
      winL[i][j] = winR[i][j] = 0;
      {  // winL
        for(int k = i + 1; k <= j; k++) {
          bool prob = mat[i][k];
          winL[i][j] += prob * winL[k][j] * sumP[i][k];
        }
      }
      {  // winR
        for(int k = i; k < j; k++) {
          bool prob = mat[j][k];
          winR[i][j] += prob * winR[i][k] * sumP[k][j];
        }
      }
    }
  }
  int ret = 0;
  for(int i = 0; i < N; i++) {
    bool ans = winR[0][i] * winL[i][N - 1];
    ret += ans > 0;
  }
  cout << ret << endl;
}

