const ll SZ = 2010;

ll n;
bool grid[SZ][SZ];
int sv[SZ][SZ], st[SZ][SZ];
vector<ll> wn[SZ];

bool find(int l, int r) {
	if (l == r - 1) return 1;
	if (sv[l][r] != -1) return sv[l][r];

	int ind;
	ind = st[l][l];
	while (ind < wn[l].size() && wn[l][ind] < r) {
		int i = wn[l][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind++;
	}

	ind = st[r][l];
	while (ind < wn[r].size() && wn[r][ind] < r) {
		int i = wn[r][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind++;
	}

	sv[l][r] = 0;
	return 0;
}

int main()
{
	fastInp;

	cin >> n;

	for (int i = 0; i < n + 3; i++) {
		for (int j = 0; j < n + 3; j++) {
			sv[i][j] = -1;
		}
	}
	for (int i = 1; i < n; i++) {
		string s;
		cin >> s;
		for (int j = 0; j < s.size(); j++) {
			if (s[j] == '1') {
				grid[i][j] = 1;
				wn[i + 1].push_back(j + 1);
			}
			else {
				grid[j][i] = 1;
				wn[j + 1].push_back(i + 1);
			}
		}
	}

	for (int i = 1; i <= n; i++) {
		sort(wn[i].begin(), wn[i].end());
	}

	for (int i = 1; i <= n; i++) {
		int curInd = wn[i].size();
		for (int j = n + 1; j >= 0; j--) {
			if (curInd != 0 && wn[i][curInd - 1] > j) curInd--;
			st[i][j] = curInd;
		}
	}

	ll ans = 0;
	for (int i = 1; i <= n; i++) {
		bool fl1 = find(0, i);
		if (fl1) {
			if (find(i, n + 1)) {
				ans++;
			}
		}
	}

	cout << ans;

	return 0;
}

