int main(){
    int n;
    cin >> n;
    vs a(n-1);
    rep(i, n-1) cin >> a[i];
    vector<vector<bool>> win(n, vector<bool>(n));
    rep1(i, n-1) rep(j, i) {
        win[i][j] = (a[i-1][j] == '1');
        win[j][i] = !(a[i-1][j] == '1');
    }
    vector<bitset<2005>> dpfl(n), dpfr(n), dpbl(n), dpbr(n);
    rep(i, n) dpfl[i][i] = dpfr[i][i] = dpbl[i][i] = dpbr[i][i] = true;
    rep1(i, n-1) rep(a, n-i) {
        int b = a + i;
        bool lok = false;
        auto &nfl = dpfl[b], &nbl = dpbl[a], &nfr = dpfr[b], &nbr = dpbr[a];
        if ((((nfl&nbl)>>(a+1))^((nfl&nbl)>>(a+i)<<(i-1))).any()) lok = true;
        if (!lok && win[a][b]) {
            if (((((nfr>>1)&nbl)>>a)^(((nfr>>1)&nbl)>>(a+i)<<i)).any()) lok = true;
        }
        nfl[a] = nbl[b] = lok;
        bool rok = false;
        if ((((nfr&nbr)>>(b-i+1))^((nfr&nbr)>>b<<(i-1))).any()) rok = true;
        if (!rok && win[b][a]) {
            if (((((nfr>>1)&nbl)>>(b-i))^(((nfr>>1)&nbl)>>b<<i)).any()) rok = true;
        }
        nfr[a] = nbr[b] = rok;
    }
    int ans = 0;
    rep(i, n) if (dpbr[0][i] && dpfl[n-1][i]) ++ans;
    cout << ans << endl;
    return 0;
}

