// bs1 is inclusive, bs2 is exclusive
std::bitset<2048> bs1[2005],bs2[2005];

void stuff(int i,int j)
{
    if ((bs2[i]&bs1[j]).any()) {
        //printf("%d %d with %c\n",i,j,ch[j][i]);
        // they overlap
        if (ch[j][i]=='0') {
            // i wins, j loses
            //printf("%d wins, %d loses\n",i,j);
            bs2[i]|=bs2[j];
        }
        else {
            // i loses, j wins
            //printf("%d loses, %d wins\n",i,j);
            bs1[j]|=bs1[i];
        }
    }
}

