            before[i][j] = before[i-1][j];
            if(before[i][i] <= j) {
                chmin(before[i][i], before[i-1][j]);
            } else if(before[i][i] <= after[i-1][j] + 1) {
                if(A[i][j] == 1) chmin(before[i][i], before[i-1][j]);
            }
        }
        int minimum = i;
        for(int j = i - 1; j >= 1; j--) {
            after[i][j] = after[i-1][j];
            if(after[minimum][j] >= minimum) {
                chmax(after[i][j], i);
            } else if(after[i][j] + 1 >= before[i][i]) {
                if(A[j][i] == 1) chmax(after[i][j], i);
            }
            if(after[i][j] == i) minimum = j;
