                    update|=chmax(dpr[i],dpr[j]);
                    if(dpr[i]>i+ma)dpr[i]=i+ma;
                }
            }
        }
        for(int i=0;i<n;++i){
            for(int j=i-1;j>=0;--j){
                if(prob[i][j]&&dpl[i]<=dpr[j]+1){
                    update|=chmin(dpl[i],dpl[j]);
                    if(dpl[i]<i-ma)dpl[i]=i-ma;
                }
            }
        }
        if(!update)break;
        ++idx;
    }
    int ans=0;
    rep(i,n)ans+=(dpl[i]==0&&dpr[i]==n-1);
    cout<<ans<<endl;
}

