set<int>st[2000];

bool f(int x, int l, int r) {
	if (x == l&&x == r)return true;
	if (x == l) {
		auto it = st[x].find(x);
		++it;
		while (*it <= r) {
			if (f(*it, x + 1, r))return true;
			++it;
		}
	}
	else if (x == r) {
		auto it = st[x].find(x);
		--it;
		while (*it >= l) {
			if (f(*it, l, x - 1))return true;
			--it;
		}
	}
	else {
		auto it = st[x].find(x);
		bool F = false;
		++it;
		while (*it <= r) {
			if (f(*it, x + 1, r)) {
				F = true;
				break;
			}
			++it;
		}
		if (F) {
			it = st[x].find(x);
			--it;
			while (*it >= l) {
				if (f(*it, l, x - 1))return true;
				--it;
			}
		}
	}
	return false;
}

