    buf += len;
    dp[i] = buf;
    buf += len;
    set_at(dp[i], i);
  }
  char *s = ALLOC (n + 1, char);
  for (i32 i = 1; i < n; ++i) {
    scanf ("%s", s);
    for (i32 j = 0; j < i; ++j) {
