      i32 s = l / 64;
      i32 t = r / 64 + 1;
      for (i32 i = s; i < t; ++i) {
        if (win[l][i] & dp[l + 1][i] & dp[r][i]) {
          set_at(dp[r], l);
          break;
        }
      }
      for (i32 i = s; i < t; ++i) {
        if (win[r][i] & dp[l][i] & dp[r - 1][i]) {
          set_at(dp[l], r);
          break;
        }
      }
    }
  }
  i32 ans = 0;
  for (i32 i = 0; i < n; ++i) {
    i32 q = i / 64;
    i32 r = i % 64;
    ans += (dp[n - 1][q] >> r) & (dp[0][q] >> r) & 1;
  }
  printf("%" PRIi32 "\n", ans);
}

