        }

    }
}

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.InputMismatchException;
import java.util.Random;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static int M;
	static long[][] win;

	static void solve()
	{
		int n = ni();
		char[][] map = new char[n][];
		for(int i = 1;i < n;i++){
			map[i] = ns().toCharArray();
		}

		M = (n>>>6)+1;
		win = new long[n][M];
		for(int i = 0;i < n;i++){
			for(int j = 0;j < i;j++){
				if(map[i][j] == '1'){
					win[i][j>>>6] |= 1L<<j;
				}else{
					win[j][i>>>6] |= 1L<<i;
				}
			}
		}

		int ct = 0;
		rcache = new int[n][n];
		lcache = new int[n][n];
		wcache = new int[n][n];
		for(int i = 0;i < n;i++){
			if(rwin(0, i) && lwin(i, n-1)){
				ct++;
			}
		}
//		for(int[] u : rcache){
//			tr(u);
//		}
		out.println(ct);
	}

	static int[][] rcache;
	static int[][] lcache;
	static int[][] wcache;
	static Random gen = new Random();

	static boolean wwin(int l, int r)
	{
		if(wcache[l][r] != 0)return wcache[l][r] == 1;
		if(r-l >= 50){
			for(int k = 0;k < 50;k++){
				int j = l + gen.nextInt(r-l);
				if(lwin(l, j) && rwin(j+1, r)){
					wcache[l][r] = 1;
					return true;
				}
			}
		}else{
			for(int j = l;j <= r-1;j++){
				if(lwin(l, j) && rwin(j+1, r)){
					wcache[l][r] = 1;
					return true;
				}
			}
		}
		wcache[l][r] = -1;
		return false;
	}

	// in [0,r] r win?
	static boolean rwin(int l, int r)
	{
		if(rcache[l][r] != 0)return rcache[l][r] == 1;
		assert l <= r;
		if(l == r){
			rcache[l][r] = 1;
			return true;
		}

		if(r-l >= 50){
			for(int k = 0;k < 50;k++){
				int i = l + gen.nextInt(r-l);
				if(win[r][i>>>6]<<~i<0){
					boolean ret = rwin(l, i) && wwin(i, r);
					if(ret){
						rcache[l][r] = 1;
						return true;
					}
				}
			}
		}else{
			for(int i = r-1;i >= l;i--){
				if(win[r][i>>>6]<<~i<0){
					boolean ret = rwin(l, i) && wwin(i, r);
					if(ret){
						rcache[l][r] = 1;
						return true;
					}
				}
			}
		}
		rcache[l][r] = -1;
		return false;
	}

	static boolean lwin(int l, int r)
	{
		if(lcache[l][r] != 0)return lcache[l][r] == 1;
		assert l <= r;
		if(l == r){
			lcache[l][r] = 1;
			return true;
		}

		if(r-l >= 50){
			for(int k = 0;k < 50;k++){
				int i = l+1 + gen.nextInt(r-l);
				if(win[l][i>>>6]<<~i<0){
					boolean ret = lwin(i, r) && wwin(l, i);
					if(ret){
						lcache[l][r] = 1;
						return true;
					}
				}
			}
		}else{
			for(int i = l+1;i <= r;i++){
				if(win[l][i>>>6]<<~i<0){
					boolean ret = lwin(i, r) && wwin(l, i);
					if(ret){
						lcache[l][r] = 1;
						return true;
					}
				}
			}
		}
		lcache[l][r] = -1;
		return false;
	}

	public static void main(String[] args) throws Exception
	{
		long S = System.currentTimeMillis();
		is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		solve();
		out.flush();
		long G = System.currentTimeMillis();
		tr(G-S+"ms");
	}

	private static boolean eof()
	{
		if(lenbuf == -1)return true;
		int lptr = ptrbuf;
		while(lptr < lenbuf)if(!isSpaceChar(inbuf[lptr++]))return false;

		try {
			is.mark(1000);
			while(true){
				int b = is.read();
				if(b == -1){
					is.reset();
					return true;
				}else if(!isSpaceChar(b)){
					is.reset();
					return false;
				}
			}
		} catch (IOException e) {
			return true;
		}
	}

	private static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	private static int readByte()
	{
		if(lenbuf == -1)throw new InputMismatchException();
		if(ptrbuf >= lenbuf){
			ptrbuf = 0;
			try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
			if(lenbuf <= 0)return -1;
		}
		return inbuf[ptrbuf++];
	}

	private static boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
//	private static boolean isSpaceChar(int c) { return !(c >= 32 && c <= 126); }
	private static int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

	private static double nd() { return Double.parseDouble(ns()); }
	private static char nc() { return (char)skip(); }

	private static String ns()
	{
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while(!(isSpaceChar(b))){
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	private static char[] ns(int n)
	{
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while(p < n && !(isSpaceChar(b))){
			buf[p++] = (char)b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	private static char[][] nm(int n, int m)
	{
		char[][] map = new char[n][];
		for(int i = 0;i < n;i++)map[i] = ns(m);
		return map;
	}

	private static int[] na(int n)
	{
		int[] a = new int[n];
		for(int i = 0;i < n;i++)a[i] = ni();
		return a;
	}

	private static int ni()
	{
		int num = 0, b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static long nl()
	{
		long num = 0;
		int b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static void tr(Object... o) { if(INPUT.length() != 0)System.out.println(Arrays.deepToString(o)); }
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.BitSet;
import java.util.Locale;
import java.util.NoSuchElementException;

public class Main {

	public static void main(String[] args) {
		new Main();
	}

	public Main() {
		FastIO io = new FastIO();
		/*
		 * 
		 * 
		 * 2
		 * 
		 * dpl[i][j][i, j]i
		 * dpr[i][j][i, j]j
		 * 
		 * dpl[i][i]=dpr[i][i]=true
		 * 
		 * dpl[i][j] = or[k = i + 1, j](A[i][k] & dpr[i + 1][k] & dpl[k][j])
		 * kik
		 * [i + 1, k]k[k, j]k[i + 1, j]k
		 * ki
		 * 
		 * dpr[i][j] = or[k = i, j - 1](A[j][k] & dpr[i][k] & dpl[k][j - 1])
		 * k[i, k]k[k, j - 1]k
		 * [i, j-1]kkj
		 * 
		 * 
		 * DPj-i
		 * 1
		 * O(N^3)BitSet
		 * index
		 * k
		 * dpl[l][r][l, r]r
		 * dpr[r][l][l, r]l
		 * 
		 * dpl[l][r] = A[r][k] & dpl[l][k] & dpr[r - 1][k](k=[l, r))
		 * dpr[r][l] = A[l][k] & dpl[l+1][k] & dpr[r][k](k=(l, r])
		 * dpl[0][i] & dpr[N-1][i]
		 *
		 */
		int N = io.nextInt();
		BitSet[] A = new BitSet[N];
		for (int i = 0;i < N;++ i) A[i] = new BitSet();
		for (int i = 1;i < N;++ i) {
			char[] s = io.next().toCharArray();
			for (int j = 0;j < i;++ j) {
				 A[i].set(j, s[j] == '1');
				 A[j].set(i, !A[i].get(j));
			}
		}
		BitSet[] dpl = new BitSet[N], dpr = new BitSet[N];
		for (int i = 0;i < N;++ i) {
			dpl[i] = new BitSet(N);
			dpl[i].set(i);
			dpr[i] = new BitSet(N);
			dpr[i].set(i);
		}
		for (int i = 1;i < N;++ i) {
			for (int r = i;r < N;++ r) {
				int l = r - i;
				for (int k = l;k < r;++ k) {
					if (dpl[l].get(r)) break;
					dpl[l].set(r, A[r].get(k) && dpl[l].get(k) && dpr[r - 1].get(k));
				}
				for (int k = l + 1;k <= r;++ k) {
					if (dpr[r].get(l)) break;
					dpr[r].set(l, A[l].get(k) && dpl[l + 1].get(k) && dpr[r].get(k));
				}
			}
		}
		int ans = 0;
		for (int i = 0;i < N;++ i) if (dpl[0].get(i) && dpr[N - 1].get(i)) ++ ans; // [0, N)i
		io.print(ans);
		io.flush();
	}

	public class FastIO {
		private final InputStream in = System.in;
		private final byte[] buffer = new byte[1024];
		private int read = 0;
		private int length = 0;
		public final PrintWriter out = new PrintWriter(System.out, false);
		public final PrintWriter err = new PrintWriter(System.err, false);

		private boolean hasNextByte() {
			if (read < length) return true;
			else {
				try {
					read = 0;
					length = in.read(buffer);
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return length > 0;
		}

		private int readByte() {
			return hasNextByte() ? buffer[read++] : -1;
		}

		private boolean isPrintableChar(int c) {
			return 33 <= c && c <= 126;
		}

		private boolean isNumber(int c) {
			return '0' <= c && c <= '9';
		}

		public boolean hasNext() {
			while (hasNextByte() && !isPrintableChar(buffer[read])) read++;
			return hasNextByte();
		}

		public char nextChar() {
			if (!hasNextByte())  throw new NoSuchElementException();
			return (char)readByte();
		}

		public String next() {
			if (!hasNext()) throw new NoSuchElementException();
			StringBuilder sb = new StringBuilder();
			int b;
			while (isPrintableChar(b = readByte())) sb.appendCodePoint(b);
			return sb.toString();
		}

		public long nextLong() {
			if (!hasNext()) throw new NoSuchElementException();
			long n = 0;
			boolean minus = false;
			int b = readByte();
			if (b == '-') {
				minus = true;
				b = readByte();
			}
			if (!isNumber(b)) throw new NumberFormatException();
			while (true) {
				if (isNumber(b)) {
					n *= 10;
					n += b - '0';
				} else if (b == -1 || !isPrintableChar(b)) return minus ? -n : n;
				else throw new NumberFormatException();
				b = readByte();
			}
		}

		public int nextInt() {
			long nl = nextLong();
			if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE) throw new NumberFormatException();
			return (int) nl;
		}

		public double nextDouble() {
			return Double.parseDouble(next());
		}

		public int[] nextInt(int width) {
			int[] ret = new int[width];
			for (int i = 0;i < width;++ i) ret[i] = nextInt();
			return ret;
		}

		public long[] nextLong(int width) {
			long[] ret = new long[width];
			for (int i = 0;i < width;++ i) ret[i] = nextLong();
			return ret;
		}

		public int[][] nextInt(int width, int height) {
			int[][] ret = new int[height][width];
			for (int i = 0, j;i < height;++ i) for (j = 0;j < width;++ j) ret[i][j] = nextInt();
			return ret;
		}

		public long[][] nextLong(int width, int height) {
			long[][] ret = new long[height][width];
			for (int i = 0, j;i < height;++ i) for (j = 0;j < width;++ j) ret[j][i] = nextLong();
			return ret;
		}

		public boolean[] nextBoolean(char T) {
			char[] s = next().toCharArray();
			boolean[] ret = new boolean[s.length];
			for (int i = 0;i < ret.length;++ i) ret[i] = s[i] == T;
			return ret;
		}

		@Override
		protected void finalize() throws Throwable {
			try {
				super.finalize();
			} finally {
				in.close();
				out.close();
				err.close();
			}
		}

		public void print(boolean b) {
			out.print(b);
		}

		public void print(char c) {
			out.print(c);
		}

		public void print(char[] s) {
			out.print(s);
		}

		public void print(double d) {
			out.print(d);
		}

		public void print(float f) {
			out.print(f);
		}

		public void print(int i) {
			out.print(i);
		}

		public void print(long l) {
			out.print(l);
		}

		public void print(Object obj) {
			out.print(obj);
		}

		public void print(String s) {
			out.print(s);
		}

		public void printf(String format, Object... args) {
			out.printf(format, args);
		}

		public void printf(Locale l, String format, Object... args) {
			out.printf(l, format, args);
		}

		public void println() {
			out.println();
		}

		public void println(boolean b) {
			out.println(b);
		}

		public void println(char c) {
			out.println(c);
		}

		public void println(char[] s) {
			out.println(s);
		}

		public void println(double d) {
			out.println(d);
		}

		public void println(float f) {
			out.println(f);
		}

		public void println(int i) {
			out.println(i);
		}

		public void println(long l) {
			out.println(l);
		}

		public void println(Object obj) {
			out.println(obj);
		}

		public void println(String s) {
			out.println(s);
		}

		public void flush() {
			out.flush();
			err.flush();
		}
	}
}

import java.util.*;

public class Main {

    static ArrayList<ArrayList<Integer>> a;
    static boolean[] used;
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        a = new ArrayList<>();
        for(int i = 0; i < n; i++) a.add(new ArrayList<>());
        for (int i = 1; i < n; i++) {
            String s = sc.next();
            for (int j = 0; j < i; j++) {
                if(s.charAt(j) == '1') a.get(i).add(j);
                else a.get(j).add(i);
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            used = new boolean[n];
            used[i] = true;
            int cnt = 0;
            cnt += dfs(i, 0, n-1);
            if(cnt == n-1) ans++;
        }
        System.out.println(ans);
        sc.close();

    }

    private static int dfs(int i, int l, int r) {
        int ret = 0;
        for(int v : a.get(i)){
            if(used[v]) continue;
            if(v < l || r < v) continue;
            ret++;
            used[v] = true;
            if(v < i) ret += dfs(v, l, i);
            else ret += dfs(v, i, r);
        }
        return ret;
    }

}

import java.io.InputStream;
import java.io.IOException;
import java.lang.NumberFormatException;
import java.util.BitSet;
import java.util.NoSuchElementException;
public class Main {
  public static void main(String[] args) {
    Main m = new Main();
    m.run();
  }
  private void run() {
    FastScanner fs = new FastScanner();
    int N = fs.nextInt();
    BitSet[] A = new BitSet[N];
    BitSet[] dpl = new BitSet[N];
    BitSet[] dpr = new BitSet[N];
    for (int i=0;i<N;i++) {
      A[i] = new BitSet();
      dpl[i] = new BitSet();
      dpr[i] = new BitSet();
      dpl[i].set(i);
      dpr[i].set(i);
    }
    for (int i=1;i<N;i++) {
      char[] s = fs.nextString().toCharArray();
      for (int j=0;j<i;j++) {
        A[i].set(j,s[j] == '1');
        A[j].set(i,s[j] == '0');
      }
    }
    for (int i=1;i<N;i++) {
      for (int l=0;l<N-i;l++) {
        int r = l+i;
        BitSet tmp = A[r].get(0,N);
        tmp.and(dpl[l]);
        tmp.and(dpr[r-1]);
        dpl[l].set(r,!tmp.get(l,r).isEmpty());
        tmp = A[l].get(0,N);
        tmp.and(dpl[l+1]);
        tmp.and(dpr[r]);
        dpr[r].set(l,!tmp.get(l+1,r+1).isEmpty());
      }
    }
    int ans = 0;
    for (int i=0;i<N;i++) {
      if (dpl[0].get(i) && dpr[N-1].get(i)) ans++;
    }
    System.out.println(ans);
  }
}
class FastScanner {
  private final InputStream ins = System.in;
  private final byte[] buf = new byte[1024];
  private int ptr = 0;
  private int len = 0;
  private boolean hasNextByte() {
    if (ptr < len) return true;
    ptr = 0;
    try {
      len = ins.read(buf);
    } catch (IOException e) {
      e.printStackTrace();
    }
    if (len < 1) return false;
    return true;
  }
  private int read() {
    if (hasNextByte()) return buf[ptr++];
    return -1;
  }
  private static boolean isPrintable(int c) {
    return 32 < c && c < 127;
  }
  private void skipUnprintable() {
    while (hasNextByte() && !isPrintable(buf[ptr])) ptr++;
  }
  public boolean hasNext() {
    skipUnprintable();
    return hasNextByte();
  }
  public String nextString() {
    if (!hasNext()) throw new NoSuchElementException();
    StringBuilder sb = new StringBuilder();
    for (int b = read();isPrintable(b);b = read()) sb.appendCodePoint(b);
    return sb.toString();
  }
  public int nextInt() {
    if (!hasNext()) throw new NoSuchElementException();
    int n = 0;
    boolean negative = false;
    int b = read();
    if (b == '-') {
      negative = true;
      b = read();
    }
    if (b < '0' || '9' < b) throw new NumberFormatException();
    while (true) {
      if (b == -1 || !isPrintable(b)) return negative ? -n : n;
      if (b < '0' || '9' < b) throw new NumberFormatException();
      n = 10*n+(b-'0');
      b = read();
    }
  }
}

