partial class Solver {

    public int SolveNaive(int n, bool[,] win) {
        var dpL = new bool[n + 1, n + 1];
        var dpR = new bool[n + 1, n + 1];

        // dpL[i][j] = [i..j]  i 
        // dpR[i][j] = [i..j]  j 
        // dpL[i][j] = exists k in [i+1,j] where dpL[k][j] && dpR[i+1][k] && win[i][k] == 1
        // dpR[i][j] = exists k in [i,j-1] where dpL[k][j-1] && dpR[i][k] && win[j][k] == 1
        for (int i = 0; i < n; i++) {
            dpL[i, i] = true;
            dpR[i, i] = true;
        }
        for (int d = 1; d <= n - 1; d++) {
            for (int i = 0; i + d < n; i++) {
                var j = i + d;
                for (int k = i + 1; k <= j; k++) {
                    if (win[i, k] && dpL[k, j] && dpR[i + 1, k]) { dpL[i, j] = true; break; }
                }
                for (int k = i; k <= j - 1; k++) {
                    if (win[j, k] && dpL[k, j - 1] && dpR[i, k]) { dpR[i, j] = true; break; }
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (dpL[i, n - 1] && dpR[0, i]) ans++;
        }
        return ans;
    }

    public int Solve(int n, bool[,] win) {
        var dpL = E.Range(0, n + 1).Select(_ => new BitSet(n + 1)).ToArray(); //new bool[n + 1, n + 1];
