        var winBit = E.Range(0, n).Select(_ => new BitSet(n + 1)).ToArray(); //new bool[n + 1, n + 1];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                winBit[i][j] = win[i, j];
            }
        }

        // dpL[i][j] = [i..j]  i 
        // dpR[i][j] = [i..j]  j 
        // dpL[j][i] = exists k in [i+1,j] where dpL[j][k] && dpR[i+1][k] && win[i][k] == 1
        // dpR[i][j] = exists k in [i,j-1] where dpL[j-1][k] && dpR[i][k] && win[j][k] == 1
        for (int i = 0; i < n; i++) {
            dpL[i][i] = true;
            dpR[i][i] = true;
        }
        for (int d = 1; d <= n - 1; d++) {
            for (int i = 0; i + d < n; i++) {
                var j = i + d;

                dpL[j][i] = (winBit[i].Slice(i + 1, j + 1) & dpL[j].Slice(i + 1, j + 1) & dpR[i + 1].Slice(i + 1, j + 1)).Any();
                dpR[i][j] = (winBit[j].Slice(i, j) & dpL[j - 1].Slice(i, j) & dpR[i].Slice(i, j)).Any();

                /*
