                    if (win[j, k] && dpL[j - 1, k] && dpR[i, k]) { dpR[i, j] = true; break; }
                }*/
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (dpL[n - 1][i] && dpR[0][i]) ans++;
        }
        return ans;
    }
