        {
            var text = Console.ReadLine();
            var n = int.Parse(text);

            if (n == 1){
                Console.WriteLine("1");
                return;
            }

            int need = 1;
            if (n <= 2){
                need = 1;
            } else if (n < 4){
                need = 1;
            } else if (n < 8){
                need = 2;
            } else if (n < 16){
                need = 3;
            } else if (n < 32){
                need = 4;
            } else if (n < 64){
                need = 5;
            } else if (n < 128){
                need = 6;
            } else if (n < 256){
                need = 7;
            } else if (n < 512){
                need = 8;
            } else if (n < 1024){
                need = 9;
            } else if (n < 2048){
                need = 10;
            }

            var list = new List<int>();
            for(int i = 0; i < n; i++){
                list.Add(0);
            }

            for(int i = 0; i < n - 1; i++)
            {
                text = Console.ReadLine();

                for(int j = 0; j < text.Length; j++){
                    var item = text.Substring(j, 1);
                    if (item == "0"){
                        list[j] = list[j] + 1;
                    }

                    if (i == (n - 2)){
                        if (item == "1"){
                            list[n - 1] = list[n - 1] + 1;
                        }
                    }
                }
            }

            for(int i = 0; i < n; i++){
                if (list[i] == (n - 1)){
                    Console.WriteLine("1");
                    return;
                }
            }

            var count = 0;
            for(int i = 0; i < n; i++){
                if (list[i] >= need){
                    count++;
                }
            }

            var mod = n % 2;

            count = count - mod;

