partial class Solver {
    public int Solve(int n, bool[,] win) {
        var dpL = E.Range(0, n + 1).Select(_ => new BitSet(n + 1)).ToArray(); //new bool[n + 1, n + 1];
        var dpR = E.Range(0, n + 1).Select(_ => new BitSet(n + 1)).ToArray(); //new bool[n + 1, n + 1];
        var winBit = E.Range(0, n).Select(_ => new BitSet(n + 1)).ToArray(); //new bool[n + 1, n + 1];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                winBit[i][j] = win[i, j];
            }
        }

        // dpL[i][j] = [i..j]  i 
        // dpR[i][j] = [i..j]  j 
        // dpL[j][i] = exists k in [i+1,j] where dpL[j][k] && dpR[i+1][k] && win[i][k] == 1
        // dpR[i][j] = exists k in [i,j-1] where dpL[j-1][k] && dpR[i][k] && win[j][k] == 1
        for (int i = 0; i < n; i++) {
            dpL[i][i] = true;
            dpR[i][i] = true;
        }
        for (int d = 1; d <= n - 1; d++) {
            for (int i = 0; i + d < n; i++) {
                var j = i + d;

                var length = winBit[i].LongArray.Length;
                for (int k = 0; k < length; k++) {
                    if ((winBit[i].LongArray[k] & dpL[j].LongArray[k] & dpR[i + 1].LongArray[k]) != 0) {
                        dpL[j][i] = true;
                        break;
                    }
                }
                for (int k = 0; k < length; k++) {
                    if ((winBit[j].LongArray[k] & dpL[j - 1].LongArray[k] & dpR[i].LongArray[k]) != 0) {
                        dpR[i][j] = true;
                        break;
                    }
                }

