                //dpR[i][j] = (winBit[j] & dpL[j - 1] & dpR[i]).Any();
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (dpL[n - 1][i] && dpR[0][i]) ans++;
        }
        return ans;
    }
