public class BitSet {
    public readonly ulong[] _array;

    public BitSet(int size) {
        _array = new ulong[(size - 1) / 64 + 1];
        BitLength = size;
    }

    public int BitLength { get; private set; }

    public int CountBit() {
        int count = 0;
        foreach (var a in _array) {
            count += PopCount(a);
        }
        return count;
    }

    public ulong[] LongArray { get { return _array; } }

    public bool Any() {
        return Array.FindIndex(_array, a => a != 0UL) >= 0;
    }

    public bool All() {
        return CountBit() == BitLength;
    }

    public BitSet Slice(int begin, int end) {
        var bitset = new BitSet(end - begin);
        var jump = begin / 64;
        var move = begin % 64;
        var arrayLength = bitset._array.Length;
        int lastIndex = 0;
        for (int i = 0; i + jump < _array.Length && i < bitset._array.Length; i++) {
            bitset._array[i] = unchecked(
                (_array[i + jump] >> move) |
                ((move > 0 && i + jump + 1 < _array.Length) ? (_array[i + jump + 1] << (64 - move)) : 0)
            );
            lastIndex = i;
        }
        var lastBit = bitset.BitLength & 63;
        if (lastBit > 0) {
            bitset._array[lastIndex] &= (1UL << lastBit) - 1;
        }
        return bitset;
    }

    public static BitSet operator <<(BitSet b, int shift) {
        if (shift == 0) return b;
        var bitset = new BitSet(b.BitLength);
        var jump = shift >> 6;
        var move = shift & 63;
        var arrayLength = bitset._array.Length;
        for (int i = jump; i < arrayLength; i++) {
            bitset._array[i] = unchecked(
                (b._array[i - jump] << move) |
                (move > 0 && i - jump - 1 >= 0 ? (b._array[i - jump - 1] >> (64 - move)) : 0)
            );
        }
        return bitset;
    }

    public static BitSet operator >>(BitSet b, int shift) {
        if (shift == 0) return b;
        var bitset = new BitSet(b.BitLength);
        var jump = shift >> 6;
        var move = shift & 63;
        var arrayLength = bitset._array.Length;
        for (int i = 0; i + jump < arrayLength; i++) {
            bitset._array[i] = unchecked(
                (b._array[i + jump] >> move) |
                (move > 0 && i + jump + 1 < arrayLength ? (b._array[i + jump + 1] << (64 - move)) : 0)
            );
        }
        var lastBit = bitset.BitLength & 63;
        if (lastBit > 0) {
            bitset._array[bitset._array.Length - 1] &= (1UL << lastBit) - 1;
        }
        return bitset;
    }


    public static BitSet operator &(BitSet b1, BitSet b2) {
        var bitset = new BitSet(b1.BitLength);
        for (int i = 0; i < bitset._array.Length; i++) {
            bitset._array[i] = b1._array[i] & b2._array[i];
        }
        return bitset;
    }

    public static BitSet operator |(BitSet b1, BitSet b2) {
        var bitset = new BitSet(b1.BitLength);
        for (int i = 0; i < bitset._array.Length; i++) {
            bitset._array[i] = b1._array[i] | b2._array[i];
        }
        return bitset;
    }

    public static BitSet operator ^(BitSet b1, BitSet b2) {
        var bitset = new BitSet(b1.BitLength);
        for (int i = 0; i < bitset._array.Length; i++) {
            bitset._array[i] = b1._array[i] ^ b2._array[i];
        }
        return bitset;
    }

    private static int PopCount(ulong x) {
        x = (x & 0x5555555555555555UL) + ((x >> 1) & 0x5555555555555555UL);
        x = (x & 0x3333333333333333UL) + ((x >> 2) & 0x3333333333333333UL);
        x = (x + (x >> 4)) & 0x0f0f0f0f0f0f0f0fUL;
        x += x >> 8;
        x += x >> 16;
        x += x >> 32;
        return (int)(x & 0x7FLU);
    }

    public static bool operator ==(BitSet b1, BitSet b2) {
        return b1.Equals(b2);
    }

    public static bool operator !=(BitSet b1, BitSet b2) {
        return !(b1 == b2);
    }

    public override bool Equals(object obj) {
        if (obj is BitSet) {
            return Equals((BitSet)obj);
        }
        return false;
    }

    public bool Equals(BitSet b) {
        if (BitLength != b.BitLength) return false;
        for (int i = 0; i < _array.Length; i++) {
            if (_array[i] != b._array[i]) return false;
        }
        return true;
    }

    public override int GetHashCode() {
        var hash = BitLength.GetHashCode();
        foreach (var a in _array) {
            hash = unchecked(hash * 31) ^ a.GetHashCode();
        }
        return hash;
    }

    public bool this[int index] {
        get {
            return (_array[index >> 6] & (1UL << (index & 63))) != 0UL;
        }
        set {
            if (value) {
                _array[index >> 6] |= 1UL << (index & 63);
            } else {
                _array[index >> 6] &= ~(1UL << (index & 63));
            }
        }
    }

    public override string ToString() {
        var b = new StringBuilder();
        for (int i = BitLength - 1; i >= 0; i--) {
            b.Append(this[i] ? '1' : '0');
        }
        return b.ToString();
    }
}

