type Bitset = array[32, uint64]

proc `[]`(bs: Bitset, i: Natural): bool =
  let i = uint64(i)
  (bs[i div 64] and 1'u64 shl (i mod 64)) == 1
proc `[]=`(bs: var Bitset, i: Natural, x: bool) =
  let i = uint64(i)
  bs[i div 64] = bs[i div 64] or uint64(x) shl (i mod 64)

proc `not`(bs: Bitset): Bitset =
  for i,b in bs:
    result[i] = not b

proc `and`(bs, cs: Bitset): Bitset =
  for i in 0..<len(bs):
    result[i] = bs[i] and cs[i]

proc `or`(bs, cs: Bitset): Bitset =
  for i in 0..<len(bs):
    result[i] = bs[i] or cs[i]

proc `shr`(bs: Bitset, x: Natural): Bitset =
  let
    n = uint64(x div 64)
    m = uint64(x mod 64)
  for i in n..<len(bs):
    result[i-n] = result[i] shr m
  for i in (n+1)..<len(bs):
    result[i-n-1] = result[i-n-1] or result[i] shl (64'u64-m)

proc `shl`(bs: Bitset, x: Natural): Bitset =
  let
    n = uint64(x div 64)
    m = uint64(x mod 64)
  for i in n..<len(bs):
    result[i] = result[i-n] shl m
  for i in (n+1)..<len(bs):
    result[i] = result[i] or result[i-n-1] shr (64'u64-m)

proc reset(bs: var Bitset) =
  for b in bs.mitems:
    b = 0

proc fill(bs: var Bitset, i,j: Natural, x: bool) =
  let
    k = j - i
    n = k div 64
    m = uint64(k mod 64)
  var cs: Bitset
  for i in 0..<n:
    cs[i] = not cs[i]
  cs[n] = 1'u64 shl m - 1'u64
  cs = cs shl i
  if x:
    bs = bs or cs
  else:
    bs = bs and not cs

