import sequtils, strutils

type
  barr = array[32, uint64]
  Bitset = distinct barr

proc `[]`(bs: Bitset, i: Natural): bool =
  (barr(bs)[i div 64] and 1'u64 shl uint64(i mod 64)) != 0
proc `[]=`(bs: var Bitset, i: Natural, x: bool) =
  if x:
    barr(bs)[i div 64] = barr(bs)[i div 64] or 1'u64 shl uint64(i mod 64)
  else:
    barr(bs)[i div 64] = barr(bs)[i div 64] and not (0'u64 shl uint64(i mod 64))

proc `not`(bs: Bitset): Bitset =
  for i,b in barr(bs):
    barr(result)[i] = not b

proc `and`(bs, cs: Bitset): Bitset =
  for i in 0..<len(barr(bs)):
    barr(result)[i] = barr(bs)[i] and barr(cs)[i]

proc `or`(bs, cs: Bitset): Bitset =
  for i in 0..<len(barr(bs)):
    barr(result)[i] = barr(bs)[i] or barr(cs)[i]

proc `shr`(bs: Bitset, x: Natural): Bitset =
  let
    n = x div 64
    m = uint64(x mod 64)
  for i in n..<len(barr(bs)):
    barr(result)[i-n] = barr(bs)[i] shr m
  for i in 0..<(len(barr(bs))-n-1):
    barr(result)[i] = barr(result)[i] or (barr(bs)[i+n+1] shl (64'u64-m))

proc `shl`(bs: Bitset, x: Natural): Bitset =
  let
    n = x div 64
    m = uint64(x mod 64)
