    let v = win_vector[0].data.len();
    for i in 0..v {
        if dp_left0[from].data[i] & win_vector[from].data[i] & dp_left_row[to].data[i] >= 1 {
            // debug!("found-l", from, to);
            return true;
        }
        if dp_left0[from].data[i] & dp_right[to].data[i] >= 1 && win_vector[from].get(to) {
            // debug!("found-r", from, to);
            return true;
        }
    }
    false
}

