    for i in 0..n {
        dp_left[i].set(i);
        dp_right[i].set(i);
        if i >= 1 {
            dp_left0[i].set(i-1);
        }
        if i+1 < n {
            dp_right0[i].set(i+1);
        }
        dp_left_row[i].set(i);
        dp_right_row[i].set(i);
    }

    for d in 1..n {
        for i in 0..n {
            if i >= d {
                if check(i, i-d, &win_vector, &dp_left0, &dp_left_row, &dp_right) {
                    dp_left[i].set(i-d);
                    if i-d >= 1 {
                        dp_left0[i].set(i-d-1);
                    }
                    dp_left_row[i-d].set(i);
                }
            }
            if i+d < n {
                if check(i, i+d, &win_vector, &dp_right0, &dp_right_row, &dp_left) {
                    dp_right[i].set(i+d);
                    if i+d+1 < n {
                        dp_right[i].set(i+d+1);
                    }
                    dp_right_row[i+d].set(i);
                }
            }
        }
    }

    let mut count = 0;
    // for i in 0..n {
    //     for j in 0..n {
