    //             dp_right[i].get(j)
    //         } else {
    //             dp_left[i].get(j)
    //         };
    //         debug!(i, j, b);
    //     }
    // }

    for i in 0..n {
        if dp_left[i].get(0) && dp_right[i].get(n-1) {
            count += 1;
        }
    }
    println!("{}", count);

}

