        self.a[q] &= !(1 << r);
    }
    fn get_at(&self, x: usize) -> bool {
        assert!(x < self.size);
        let (q, r) = quot_rem(x);
        (self.a[q] >> r) & 1 == 1
    }
    fn any(&self) -> bool {
        self.a.iter().any(|a| *a != 0)
    }
    fn fix(&mut self) {
        let (q, r) = quot_rem(self.size);
        if r != 0 {
            self.a[q] &= (1 << r) - 1;
        }
    }
    fn clear(&mut self) {
        let len = self.a.len();
        self.a.clear();
        self.a.resize(len, 0);
    }
    fn truncate(&mut self, len: usize) {
        if len >= self.size {
            return;
        }
        let w = bit_size();
        self.a.truncate((len + w - 1) / w);
        self.size = len;
        self.fix();
    }
    fn shift_left(&self, rhs: usize) -> Self {
        let (q, r) = quot_rem(rhs);
        let mut ans = BitSet::new(self.size + rhs);
        if r == 0 {
            for (x, y) in ans.a[q..].iter_mut().zip(self.a.iter()) {
                *x = *y;
            }
        } else {
            let w = bit_size();
            let mut prev = 0;
            for (x, y) in ans.a[q..].iter_mut().zip(self.a.iter()) {
                *x = (*y << r) | (prev >> (w - r));
                prev = *y;
            }
            *ans.a.last_mut().unwrap() |= prev >> (w - r);
        }
        ans.fix();
        ans
    }
    fn bitwise_or(&self, rhs: &Self) -> Self {
        let (x, y) = if self.size >= rhs.size {(self, rhs)} else {(rhs, self)};
        let mut a = x.a.clone();
        for (a, y) in a.iter_mut().zip(y.a.iter()) {
            *a |= *y;
        }
        BitSet {
            size: x.size,
            a: a,
        }
    }
    fn bitwise_and(&self, rhs: &Self) -> Self {
        let (x, y) = if self.size <= rhs.size {(self, rhs)} else {(rhs, self)};
        let mut a = x.a.clone();
        for (a, y) in a.iter_mut().zip(y.a.iter()) {
            *a &= *y;
        }
        BitSet {
            size: x.size,
            a: a,
        }
    }
    fn bitwise_or_assign(&mut self, rhs: &Self) {
        if self.size < rhs.size {
            self.size = rhs.size;
            self.a.resize(rhs.a.len(), 0);
        }
        for (a, b) in self.a.iter_mut().zip(rhs.a.iter()) {
            *a |= *b;
        }
    }
    fn bitwise_and_assign(&mut self, rhs: &Self) {
        if self.size > rhs.size {
            self.size = rhs.size;
            self.a.resize(rhs.a.len(), 0);
        }
        for (a, b) in self.a.iter_mut().zip(rhs.a.iter()) {
            *a &= *b;
        }
    }
}

use std::io::Read;

fn run() {
    let mut s = String::new();
    std::io::stdin().read_to_string(&mut s).unwrap();
    let mut it = s.trim().split_whitespace();
    let n: usize = it.next().unwrap().parse().unwrap();
    let mut dp = vec![BitSet::new(n); n];
    let mut win = vec![BitSet::new(n); n];
    for i in 1..n {
        let s: Vec<char> = it.next().unwrap().chars().collect();
        for j in 0..i {
            if s[j] == '1' {
                win[i].set_at(j);
            } else {
                win[j].set_at(i);
            }
        }
    }
    for i in 0..n {
        dp[i].set_at(i);
    }
    for len in 2..(n + 1) {
        for l in 0..(n + 1 - len) {
            let r = l + len - 1;
            let w = bit_size();
            let s = l / w;
            let t = r / w + 1;
            if win[l].a[s..t].iter().zip(dp[l + 1].a[s..t].iter()).zip(dp[r].a[s..t].iter()).any(|((a, b), c)| (*a & *b & *c) != 0) {
                dp[r].set_at(l);
            }
            if win[r].a[s..t].iter().zip(dp[l].a[s..t].iter()).zip(dp[r - 1].a[s..t].iter()).any(|((a, b), c)| (*a & *b & *c) != 0) {
                dp[l].set_at(r);
            }
        }
    }
    let mut ans = 0;
    let valid = dp[n - 1].bitwise_and(&dp[0]);
    for i in 0..n {
        if valid.get_at(i) {
            ans += 1;
        }
    }
    println!("{}", ans);
}

fn main() {
    run();
}

