fn check(from: usize, to: usize, win_vector: &Vec<BitVector>, dp_left_row: &Vec<BitVector>, dp_right_row: &Vec<BitVector>) -> bool {
    let v = win_vector[0].data.len();
    let next = ifv!(from < to, from+1, from-1);
    for i in 0..v {
        if dp_left_row[next].data[i] & win_vector[from].data[i] & dp_right_row[to].data[i] >= 1 {
            return true;
        }
    }
    false
}

fn main() {
    input! {
        n: usize,
        a: [chars; n-1]
    };

    let mut table = dvec!(false; n, n);
    for i in 0..n {
        for j in 0..i {
            table[i][j] = ifv!(a[i-1][j] == '0', false, true);
            table[j][i] = table[i][j] ^ true;
        }
        table[i][i] = true;
    }

    let mut win_vector = vec![BitVector::new(n); n];
    for i in 0..n {
        for j in 0..n {
            if table[i][j] {
                win_vector[i].set(j);
            }
        }
    }

    let mut dp_left = vec![BitVector::new(n); n];
    let mut dp_right = vec![BitVector::new(n); n];
    let mut dp_left_row = vec![BitVector::new(n); n];
    let mut dp_right_row = vec![BitVector::new(n); n];
    for i in 0..n {
        dp_left[i].set(i);
        dp_right[i].set(i);
        dp_left_row[i].set(i);
        dp_right_row[i].set(i);
    }

    for d in 1..n {
        for i in 0..n {
            if i >= d {
                if check(i, i-d, &win_vector, &dp_right_row, &dp_left_row) {
                    dp_left[i].set(i-d);
                    dp_left_row[i-d].set(i);
                }
            }
            if i+d < n {
                if check(i, i+d, &win_vector, &dp_left_row, &dp_right_row) {
                    dp_right[i].set(i+d);
                    dp_right_row[i+d].set(i);
                }
            }
        }
    }

    let mut count = 0;
    // for i in 0..n {
    //     for j in 0..n {
    //         let b = if i < j {
    //             dp_right[i].get(j)
    //         } else {
    //             dp_left[i].get(j)
    //         };
    //         debug!(i, j, b);
    //     }
    // }

    for i in 0..n {
        if dp_left[i].get(0) && dp_right[i].get(n-1) {
            count += 1;
        }
    }
    println!("{}", count);

}

