	for (int i = 0; i < n; i++) {
	    for (int j = 0; j < n; j++) {
		if (sp[i][j] == '.') {
		    printf(" ");
		} else {
		    printf("%c", sp[i][j]);
		}
	    }
	    puts("");
	}   
    }
    return 0;
}

