    rep(i,n){
        rep(i,105) rep(j,105) a[i][j] = ' ';
        int inp;
        cin >> inp;
        solve(a, inp, 0, 104, 0);
        for(int i = 104 - inp; i <= 104; i++){
            rep(j,inp){
                cout << a[i][j];
            }
            cout << endl;
        }
    }
}

