    while (dataset--) {
        cin >> n;

        vector<vector<bool> > map(n, vector<bool>(n, false));
        int d = 0, x = 0, y = n - 1, l = n - 1;

        for (int i = 0; i < 3; ++i) {
            for (int j = 0; j < l; ++j) {
                map[y][x] = true;
                x += dx[d];
                y += dy[d];
            }
            next(d);
        }

