void next(int x,int y,int r){
	int sum=0;
	int nx[4]={0,1,0,-1};
	int ny[4]={-1,0,1,0};
	for(int i=0;i<4;i++)if(map[x+nx[i]][y+ny[i]]=='#')sum++;
