		map[x][y]='#';
		if(map[x+nx[r]*2][y+ny[r]*2]=='#'){
			r=(r+1)%4;
			next(x+nx[r],y+ny[r],r);
		}
		else{
			next(x+nx[r],y+ny[r],r);
		}
	}
}

