        if(isSpace(x + dx[i], y + dy[i]) ||
           isSpace(x + dx[i] + dx[i], y + dy[i] + dy[i]))
          break;

        while(!isSpace(x + dx[i] + dx[i], y + dy[i] + dy[i])){
          x = x + dx[i]; y = y + dy[i];
          ans[y][x] = ' ';
        }

        i = (i + 1) % 4;
      }
      REP(i,n) puts(ans[i]);
    }
  }
  return 0;
}

