const int dir[][2] = {{1,0},{0,1},{-1,0},{0,-1}};

void solve(int m){
    string s;
    REP(i,m) s += " ";
    vector<string> v(m, s);

    int x = 0, y = m-1, d = 3, w = m, h = m, c = 0;
    v[y][x] = '#';
    while(1){
        int mx = x + dir[d][0];
        int my = y + dir[d][1];
        if(mx < 0 || my < 0 || mx >= w || my >= h){
            d = (d+1)%4;
            if(++c >= 2) break;
            continue;
        }
        bool f = true;
        REP(i,4){
            int mx2 = mx + dir[i][0];
            int my2 = my + dir[i][1];
            if(mx2 < 0 || my2 < 0 || mx2 >= w || my2 >= h) continue;
            if(x == mx2 && y == my2) continue;
            if(v[my2][mx2] == '#'){
                f = false; break;
            }
        }
        if(!f){
            d = (d+1)%4;
            if(++c >= 2) break;
            continue;
        }
        c = 0;
        x += dir[d][0], y += dir[d][1];
        v[y][x] = '#';
        //prfep(v);
    }
    FOREACH(it,v) cout << *it << endl;
}

int main(){
    int n, m;
    cin >> n;
    bool f = false;
    while(cin >> m){
        if(f) cout << endl;
        solve(m);
        f = true;
    }
    return 0;
}

