			cout<<"#"<<endl;
			continue;
		}
		memset(sp,0,sizeof(sp));

		int y=n-1,x=0,d=0;
		int c=0;
		while(true){


			if(d==0){
				if(sp[y-1][x+1]==1)break;
				int cy=y;
				while(cy>=1){
					if(cy>=2 && sp[cy-2][x]==1)break;
					--cy;
