        if(inp == 1) cout << '#' << endl;
        else if(inp == 2) cout << "# " << endl << "# " << endl;
        else{
            for(int i = 104 - inp + 1; i <= 104; i++){
                rep(j,inp){
                    cout << a[i][j];
                }
                cout << endl;
            }
        }
    }
}

