      for(int j=0;j<n;j++)grid[i][j] = false;
    }

    bool f[4];
    for(int i=0;i<4;i++)f[i] = true;
    int x = 0, y = n-1;
    grid[y][x] = true;

    while(f[0] || f[1] || f[2] || f[3]){
      for(int i=0;i<4;i++)f[i] = false;

      while(1){
	y--;
	if(y < 0 || (x+1 < n && grid[y][x+1]) || (x-1 >= 0 && grid[y][x-1]) || (y-1 >= 0 && grid[y-1][x]) ){
	  y++;
	  break;
	}
	f[0] = true;
	grid[y][x] = true;
      }

     while(1){
	x++;
	if(x >= n || (y+1 < n && grid[y+1][x]) || (y-1 >= 0 && grid[y-1][x]) || (x+1 < n && grid[y][x+1]) ){
	  x--;
	  break;
	}
	f[1] = true;
	grid[y][x] = true;
      }

     while(1){
	y++;
	if(y >= n ||(x-1 >= 0 && grid[y][x-1]) || (x+1 < n && grid[y][x+1]) || (y+1 < n && grid[y+1][x]) ){
	  y--;
	  break;
	}
	f[2] = true;
	grid[y][x] = true;
      }

      while(1){
	x--;
	if(x < 0 || (y+1 < n && grid[y+1][x]) || (y-1 >= 0 && grid[y-1][x]) || (x-1 >= 0 && grid[y][x-1]) ){
