	else
	{
		if(dx == 0 && dy == -1) 	rec(g, x, y,  1, 0, c + 1);
		else if(dx == 1 && dy == 0)	rec(g, x, y,  0, 1, c + 1);
		else if(dx == 0 && dy == 1)	rec(g, x, y, -1, 0, c + 1);
		else						rec(g, x, y, 0, -1, c + 1);
	}
}

int main()
{
	int d;
	cin >> d;

	for(int n; d && cin >> n; d--)
	{
		vector<vector<char> > g(n, vector<char>(n, ' '));

		rec(g, 0, n - 1, 0, -1, 0);

		if(n % 2 == 0) g[n / 2][n / 2] = ' ';

		rep(y, n) rep(x, n) cout << g[x][y] << (x + 1 == n ? "\n" : "");
	}
}

