#include <bits/stdc++.h>

using namespace std;

char MapData[100][100];
bool Finished;
int N;

bool check(int x, int y, int i) {

	if (i == 0 || i==2) {
		if (MapData[y][x-1] == ' ' && MapData[y][x+1] == ' ') {
			return true;
		}
	}
	else if (i == 1 || i==3) {
		if (MapData[y-1][x] == ' ' && MapData[y+1][x] == ' ') {
			return true;
		}
	}
	return false;
}

void solve(int x, int y, int direction) {
	int dx[] = { 0,2,0,-2 };
	int dy[] = { -2,0,2,0 };
	int currentX, currentY, i, cnt;

	cnt = 0;
	for (i = direction; cnt < 4; i++) {
		currentX = x + dx[i%4];
		currentY = y + dy[i%4];
		if (currentX<0 || currentX>N-1 || currentY<0 || currentY>N-1) {
			continue;
		}
		if (MapData[currentY][currentX] == ' ') {
			switch (i%4)
			{
			case 0:currentY++; break;
			case 1:currentX--; break;
			case 2:currentY--; break;
			case 3:currentX++; break;
			}
			if (MapData[currentY][currentX] == ' ' && check(currentX, currentY, i%4)) {
				MapData[currentY][currentX] = '#';
				solve(currentX, currentY,i%4);
			}
		}
		if (Finished) {
			return;
		}
		cnt++;
	}
	if (cnt == 4) {
		Finished = true;
	}
}

int main() {
	int d;

	cin >> d;
	while (d--) {
		cin >> N;

		memset(MapData, ' ', sizeof(MapData));
		Finished = false;
		for (int i = 0; i < N; i++) {
			MapData[0][i] = '#';
			MapData[i][0] = MapData[i][N - 1] = '#';
			MapData[N - 1][i] = '#';
		}
		if (N > 1) {
			MapData[N - 1][1] = ' ';
		}
		if (N > 4) {
			solve(2, N - 1, 0);
		}


		for (int i = 0; i < N; i++) {
