int main()
{
	int d;

	while( cin >> d ){
		for( int i = 0; i < d; ++i ){
			int n;
			cin >> n;

			int size = n + 4;
			char* map = new char[ size * size ]();
			for( int i = 0; i < size; ++i ){
				map[ i ] = map[ ( size - 1 ) * size + i ] = map[ size * i ] = map[ ( i + 1 ) * size - 1 ] = '#';
			}
			int x = 2, y = n + 1;
			int dir = 0;
			int dirCount = 0;
			int straightCount = 0;
			while( dirCount < 2 ){
				map[ y * size + x ] = '#';
				++straightCount;
				int nx, ny;
				switch( dir ){
					case 0: nx = x; ny = y - 2; break;
					case 1: nx = x + 2; ny = y; break;
					case 2: nx = x; ny = y + 2; break;
					case 3: nx = x - 2; ny = y; break;
				}
				if( map[ ny * size + nx ] == '#' ){
					if( straightCount < 3 ){
						break;
					}
					dir = ( dir + 1 ) % 4;
					++dirCount;
					straightCount = 0;
					continue;
				}else{
					dirCount = 0;
				}
				switch( dir ){
					case 0: y -= 1; break;
					case 1: x += 1; break;
					case 2: y += 1; break;
					case 3: x -= 1; break;
				}
			}

			for( int p = 2; p < size - 2; ++p ){
				for( int q = 2; q < size - 2; ++q ){
					cout << map[ p * size + q ];
				}
				cout << endl;
			}

			delete[] map;
		}
	}

	return 0;
}

