using namespace std;

bool isOver(int x, int y, int n)
{
	if (x<0||n<=x||y<0||n<=y) return true;
	return false;
}

int main()
{
	int d, i, n, j, k;
	int x, y, dir;
	int turn;
	vector<string> s;
	int dx[4] = {0, 1, 0, -1};
	int dy[4] = {-1, 0, 1, 0};
	int chkx[2][4] = { {-1, 1, -1, -1}, {1, 1, 1, -1} };
	int chky[2][4] = { {-1, -1, 1, -1}, {-1, 1, 1, 1} };
	cin>>d;
	for (i = 0; i < d; i++) {
		cin>>n;
		s.resize(n);
		for (j = 0; j < n; j++)
			for (k = 0; k < n; k++)
				s[k][j] = ' ';
		x = 0; y = n-1; dir = 0;
		turn = 0;
		k = 0;
		while (turn<4) {
			s[x][y] = '#';
			if ( isOver(x+dx[dir], y+dy[dir], n) ) {
				dir = (dir+1)%4;
				turn++;
				continue;
			}
			if ( !isOver(x+2*dx[dir], y+2*dy[dir], n) ) {
				if (s[x+2*dx[dir]][y+2*dy[dir]] == '#') {
					dir = (dir+1)%4;
					turn++;
					continue;
				}
			}
			if ( !isOver(x+chkx[0][dir], y+chky[0][dir], n) )
				if (s[x+chkx[0][dir]][y+chky[0][dir]] == '#') break;
			if ( !isOver(x+chkx[1][dir], y+chky[1][dir], n) )
				if (s[x+chkx[1][dir]][y+chky[1][dir]] == '#') break;
			x += dx[dir];
			y += dy[dir];
			turn = 0;
		}
		if (i) cout<<endl;
		for (j = 0; j < n; j++) {
			for (k = 0; k < n; k++) {
				cout<<s[k][j];
			}
			cout<<endl;
		}
	}
	return 0;
}

