constexpr int dx[4] = {-1, 0, 1, 0};
constexpr int dy[4] = {0, 1, 0, -1};

constexpr char SHARP = '#';
constexpr char SPACE = ' ';

void draw(int width, int x, int y, vector<string>& a) {
    if (width == 1) {
        a[x][y] = SHARP;
    } else if (width == 2) {
        a[x][y] = a[x+1][y] = a[x][y+1] = SHARP;
    } else if (width == 3) {
        a[x][y] = a[x][y+1] = a[x][y+2] = SHARP;
        a[x+1][y] = a[x+1][y+2] = SHARP;
        a[x+2][y] = a[x+2][y+2] = SHARP;
    } else if (width == 4) {
        a[x][y] = a[x][y+1] = a[x][y+2] = a[x][y+3] = SHARP;
        a[x+1][y] = a[x+1][y+3] = SHARP;
        a[x+2][y] = a[x+2][y+3] = SHARP;
        a[x+3][y] = a[x+3][y+2] = a[x+3][y+3] = SHARP;
    } else {
        for (int j = 0; j < width; ++j) {
            a[x][y+j] = SHARP;
        }
        for (int j = 1; j < width; ++j) {
            a[x+j][y] = SHARP;
            a[x+j][y+width-1] = SHARP;
        }
        for (int j = 2; j < width; ++j) {
            a[x+width-1][y+j] = SHARP;
        }
        a[x+width-2][y+2] = SHARP;
        draw(width-4, x+2, y+2, a);
    }
}

