const int dx[4] = {-1,0,+1,0};
const int dy[4] = {0,-1,0,+1};

class Solve{
private:
  int d;
  int imap[110][110];
public:

  Solve(int n):d(n){}
  ~Solve(){}

  int Connect(P p){
    int cnt = 0;
    rep(i,4){
      int nx = p.F+dx[i],ny = p.S+dy[i];
      if(!(0<=nx && nx <d && 0<= ny && ny < d))continue;
      if(imap[ny][nx] == 1)cnt++;
    }
    return cnt;
  }


  void display(){
      cout << "--- in display ---" << endl;
      rep(i,d){
    rep(j,d){
      if(imap[i][j] == 1)cout << '#';
      else cout << ' ';
    }
    cout << endl;
      }
      cout << endl;
    }


  void run(){
    rep(i,d+1)rep(j,d+1)imap[i][j] = 0;

    rep(i,d){
      imap[0][i] = 1;
      imap[i][0] = 1;
      imap[i][d-1] = 1;
    }




    P pos = P(d-1,d-1);
    int dir = 0;
    int cnt = 0;
    bool fin = false;
    while(true){

      int nx,ny;
      nx  = pos.F,ny = pos.S;
      while(true){
    //cout << "nx = " << nx << ",ny = "<< ny << ",dir = " << dir << endl;
    //display();


    if(!(Connect(P(nx,ny)) == 2 || Connect(P(nx,ny)) == 1)){
      imap[ny-dy[dir]][nx-dx[dir]] = 0;
      //cout << "Lase nx = " << nx << ",ny = "<< ny << endl;
      //cout << "FIN! connnect = " << Connect(P(nx,ny)) << endl;
      fin = true;
      break;
    }

    if(imap[ny+dy[dir]][nx+dx[dir]] == 1){
      //cout << "IN nx = " << nx+dx[dir] << ",ny = " << ny+dy[dir] << endl;
      imap[ny][nx] = 0;
      cnt--;
      break;
    }
    imap[ny][nx] = 1;
    cnt++;  


    nx += dx[dir],ny += dy[dir];
      }
      //cout << "------ cnt = " << cnt << endl;
      if(fin){
    //cout << "dir = " << dir << endl;
    break;
      }
      if(cnt == 0)break;
      cnt =0;

      pos.F = nx-dx[dir],pos.S = ny-dy[dir];
      dir++;
      dir = dir%4;
    }

    rep(i,d){
      rep(j,d){
    if(imap[i][j] == 1)cout << '#';
    else cout << ' ';
      }
      cout << endl;
    }


  }


};


int main(){
  int T;
  cin >> T;
  while(T-- > 0){
    int _n;
    cin >> _n;
    Solve ans = Solve(_n);
    ans.run();
    if(T != 0)cout << endl;
  }
return 0;
}

