// 移動ベクトルという概念
// 今埋めようとしている周囲に#が1個のみならば埋める、
// そうでなければ方向転換.方向転換後1つも埋められないなら終わり

#include<iostream>
using namespace std;

int dir[4][2] = {{-1,0} , {0,1}, {1,0}, {0,-1}};
char mat[100][100];
int n;

bool ok(int x, int y){
    int count = 0;
    for(int i = 0; i < 4; i++){
        int ni = x + dir[i][0];
        int nj = y + dir[i][1];
        if(0 <= ni && ni < n && 0 <= nj && nj < n && mat[ni][nj] == '#'){
            count++;
        }
    }
    return count <= 1;
}

int main(){
    int d;
    cin >> d;

    for(int c = 0; c < d; c++){
        if(c)   cout << endl;

        cin >> n;

        for(int i = 0; i < 100; i++){
            for(int j = 0; j < 100; j++){
                mat[i][j] = ' ';
            }
        }

        int move = 0;

        int i = n, j = 0;
        while(1){
            int ni = i + dir[move][0];
            int nj = j + dir[move][1];

            if(0 <= ni && ni < n & 0 <= nj && nj < n && ok(ni, nj)){
                mat[ni][nj] = '#';
                i = ni;
                j = nj;
            }else{
                move = (move+1)%4;
                ni = i + dir[move][0];
                nj = j + dir[move][1];
                if(0 <= ni && ni < n & 0 <= nj && nj < n && ok(ni, nj)){
                    mat[ni][nj] = '#';
                    i = ni;
                    j = nj;
                }else{
                    break;
                }
            }
        }

        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                cout << mat[i][j];
            }
            cout << endl;
        }
    }

    return 0;
}

#include <iostream>
using namespace std;

int main()
{
	int d;

	while( cin >> d ){
		for( int i = 0; i < d; ++i ){
			int n;
			cin >> n;

			int size = n + 4;
			char* map = new char[ size * size ]();
			for( int j = 0; j < size; ++j ){
				for( int k = 0; k < size; ++k ){
					map[ j * size + k ] = ' ';
				}
			}
			for( int j = 0; j < size; ++j ){
				map[ j ] = map[ ( size - 1 ) * size + j ] = map[ size * j ] = map[ ( j + 1 ) * size - 1 ] = '#';
			}
			for( int j = 2; j < size - 2; ++j ){
				map[ j * size + 2 ] = '#';
			}
			int x = 2, y = 2;
			int dir = 1;
			int dirCount = 0;
			int straightCount = 0;
			while( dirCount < 2 ){
				map[ y * size + x ] = '#';
				++straightCount;
				int nx, ny;
				switch( dir ){
					case 0: nx = x; ny = y - 2; break;
					case 1: nx = x + 2; ny = y; break;
					case 2: nx = x; ny = y + 2; break;
					case 3: nx = x - 2; ny = y; break;
				}
				if( map[ ny * size + nx ] == '#' ){
					if( straightCount < 3 ){
						break;
					}
					dir = ( dir + 1 ) % 4;
					++dirCount;
					straightCount = 0;
					continue;
				}else{
					dirCount = 0;
				}
				switch( dir ){
					case 0: y -= 1; break;
					case 1: x += 1; break;
					case 2: y += 1; break;
					case 3: x -= 1; break;
				}
			}

			for( int p = 2; p < size - 2; ++p ){
				for( int q = 2; q < size - 2; ++q ){
					cout << map[ p * size + q ];
				}
				cout << endl;
			}

			delete[] map;
		}
	}

	return 0;
}

