int n,d,i,j,st,cc;
char sp[101][101];

int ismove(){
	if(st==0 && i>0 && sp[i-1][j]!=' ')return 1;
	if(st==1 && j<d-1 && sp[i][j+1]!=' ')return 1;
	if(st==2 && i<d-1 && sp[i+1][j]!=' ')return 1;
	if(st==3 && j>0 && sp[i][j-1]!=' ')return 1;
	return 0;
}

void move(){
	if(st==0)i--;
	if(st==1)j++;
	if(st==2)i++;
	if(st==3)j--;
}

void mark(){
	if(st==0)sp[i][j+1]=' ';
	if(st==1)sp[i+1][j]=' ';
	if(st==2)sp[i][j-1]=' ';
	if(st==3)sp[i-1][j]=' ';
}

int main(){
	for(scanf("%d",&n);n>0;n--){
		scanf("%d",&d);
		for(i=0;i<d;i++){for(j=0;j<d;j++)sp[i][j]='\0';}
		i=d-1;j=0;st=0;cc=0;
		sp[i][1]=' ';
		while(1){
			sp[i][j]='#';
			if(ismove()){
				if(cc>0)mark();
				cc++;
				move();
			}
			else{
				if(cc==0)break;
				st=(st+1)%4;
				cc=0;
			}
		}
		for(i=0;i<d;i++){{for(j=0;j<d;j++)printf("%c",sp[i][j]);}printf("\n");}
		if(n>1)printf("\n");
	}
	return 0;
}

#include<stdio.h>
#define X '#'

void DrawSpiral(void);
int DrawTopLine(void);
int DrawBottomLine(void);
int DrawLeftLine(void);
int DrawRightLine(void);

int x, y, n;
char Spiral[100][100];

int main(){
  int i, j, k, d;

  scanf("%d", &d);

  for(i=0; i<d; ++i){
    scanf("%d", &n);

    for(j=0; j<n; j++){
      for(k=0; k<n; k++)
        Spiral[j][k] = ' ';
    }

    x = n-1;
    y = 0;
    DrawSpiral();    

    if(i != 0) printf("\n");

    for(j=0; j<n; j++){
      for(k=0; k<n; k++)
        printf("%c", Spiral[j][k]);
      printf("\n");
    }
  }

  return 0;
}

void DrawSpiral(void){
  while(1){
    if(DrawLeftLine() == 1) break;
    if(DrawTopLine() == 1) break;
    if(DrawRightLine() == 1) break;
    if(DrawBottomLine() == 1) break;
  }
}

int DrawTopLine(void){
  if(n == 2){
    Spiral[x][y+1] = X;
    return 1;
  }if(y < n-2 && Spiral[x][y+2] == X){
    return 1;
  }else if(x < n-3 && Spiral[x][y+3] == X){
    Spiral[x][y+1] = X;
    return 1;
  }

  while(y < n-1 && Spiral[x][y+2] != X){
    Spiral[x][y] = X;
    y++;
  }
  Spiral[x][y] = X;

  return 0;
}

int DrawBottomLine(void){
  if(y > 1 && Spiral[x][y-2] == X){
    return 1;
  }else if(x > 2 && Spiral[x][y-3] == X){
    Spiral[x][y-1] = X;
    return 1;
  }

  while(y > 0 && Spiral[x][y-2] != X){
    Spiral[x][y] = X;
    y--;
  }
  Spiral[x][y] = X;

  return 0;
}

int DrawLeftLine(void){
  if(n == 1){
    Spiral[0][0] = X;
    return 1;
  }else if(x > 1 && Spiral[x-2][y] == X){
    return 1;
  }else if(x > 2 && Spiral[x-3][y] == X){
    Spiral[x-1][y] = X;
    return 1;
  }

  while(x > 0 && Spiral[x-2][y] != X){
    Spiral[x][y] = X;
    x--;
  }
  Spiral[x][y] = X;

  return 0;
}

int DrawRightLine(void){
  if(x < n-2 && Spiral[x+2][y] == X){
    return 1;
  }else if(x < n-3 && Spiral[x+3][y] == X){
    Spiral[x+1][y] = X;
    return 1;
  }

  while(x < n-1 && Spiral[x+2][y] != X){
    Spiral[x][y] = X;
    x++;
  }
  Spiral[x][y] = X;

  return 0;
}

#include <stdio.h>

#define INV(A,N) \
  ( (N) - (A) - 1 )
#define MIN(A,B) \
  ( (A) < (B) ? (A) : (B) )
#define MINI(A,B,N) \
  ( MIN( INV(A,N), \
         MIN( INV(B,N), \
              MIN(A,B) ) ) )

/** Application main entry point. */
int main (
  int     argc,
  char  * argv[ ]
  )
{
  int x, y;
  int d;

  scanf ( "%d", &d );
  while ( d-- )
  {
    int n;

    scanf ( "%d", &n );

    for ( y = 0; y < n; ++y )
    {
      for ( x = 0; x < n; ++x )
      {
        int ch = ' ';

        if ( MINI( x, y, n ) % 2 == 0 ) ch = '#';
        if ( x == n - y )
        {
          if ( y * 2 >  n ) ch = '#';
          if ( y * 2 >= n && !( INV( n, y ) % 2 ) ) ch = ' ';
        }

        putchar ( ch );
      }
      puts ( "" );
    }

    if ( d )  puts ( "" );
  }

  return ( 0 );
}

