int make_pattern(int n)
{
  int last,ret;

  if(n == 2)
  {  pattern[0]=2;
     return(1);
  }
  last=(n & 1)?1:2;

  ret=0;
  n--;
  pattern[0]=n;
  pattern[1]=n;
  pattern[2]=n;
  ret = 3;

  while(n > last+2)
  { n -= 2;
    pattern[ret++]=n;
    pattern[ret++]=n;
  }
  pattern[ret++]=last;

  return(ret);
}

