        while (1){
            // ツ湘」ツ古シツつォ
            cnt = 0;
            while (y > 0){
                y--;
                if (y < 1 || screw[y - 1][x] == ' '){
                    screw[y][x] = '#';
                    cnt++;
                }
                else {
                    y++;
                    break;
                }
            }
            if (cnt < 2){
                break;
            }

            // ツ右ツ古シツつォ
            cnt = 0;
            while (x < n - 1){
                x++;
                if (x >= n - 1 || screw[y][x + 1] == ' '){
                    screw[y][x] = '#';
                    cnt++;
                }
                else {
                    x--;
                    break;
                }
            }
            if (cnt < 2){
                break;
            }

            // ツ可コツ古シツつォ
            cnt = 0;
            while (y < n - 1){
                y++;
                if (y >= n - 1 || screw[y + 1][x] == ' '){
                    screw[y][x] = '#';
                    cnt++;
                }
                else {
                    y--;
                    break;
                }
            }
            if (cnt < 2){
                break;
            }

            // ツ債カツ古シツつォ
            cnt = 0;
            while (x > 0){
                x--;
                if (x < 1 || screw[y][x - 1] == ' '){
                    screw[y][x] = '#';
                    cnt++;
                }
                else {
                    x++;
                    break;
                }
            }
            if (cnt < 2){
                break;
            }
        }

        for (j = 0; j < n; j++){
            printf("%s\n", screw[j]);
        }
    }


    return (0);
}

