int main() {
	int n,size,i,j,k;
	int u, d, l, r, b[100][100];
	fscanf(stdin, "%d", &n);
	while (n--) {
		memset(b, 0, sizeof(b));
		fscanf(stdin, "%d", &size);
		u = 1; i = size-1; 
		k = size*size;
		d = l = r = j = 0;
		while (k--) {
			if (u) {
				//if (b[i-1][j]) { b[i][j] = 0; break; }
				b[i][j] = 1;
				if (i == 0 || (i-2>=0&&b[i - 2][j]))  u = 0, r = 1;
				else i--;
			}
			else if (r) {
				if (b[i][j + 1]) { b[i][j] = 0; break; }
				b[i][j] = 1;
				if (j == size-1 || (j + 2 <= size-1 && b[i][j+2])) r = 0, d = 1;
				else j++;
			}
			else if (d) {
				//if (b[i+1][j]) { b[i][j] = 0; break; }
				b[i][j] = 1;
				if (i == size-1 || (i + 2 <= size-1 && b[i + 2][j]))  d = 0, l = 1;
				else i++;
			}
			else if (l) {
				if (b[i][j - 1]) { b[i][j] = 0; break; }
				b[i][j] = 1;
				if (j == 0 || (j - 2 >= 0 && b[i][j - 2])) l = 0, u = 1;
				else j--;
			}
		}
		for (i = 0; i < size; i++, puts(""))
			for (j = 0; j < size; j++)
				fprintf(stdout,"%c", b[i][j] == 1 ? '#' : ' ');
	}
	return 0;
}

#include<stdio.h>
#define N 10000
#define M 101

void guruguru(
    char map[101][101],int n,
    int x,int y,
    int vector,int length,int count){

  if(count>=length-1)
    return;
  else if(vector==8){
    y--;
    if(y<0 || map[y-1][x]=='#'){
      vector=6;
      y++;
      x++;
    }
    map[y][x]='#';
    count++;
    guruguru(map,n,x,y,vector,length,count);
    return;
  }
  else if(vector==6){
    x++;
    if(x>=n || map[y][x+1]=='#'){
      vector=2;
      y++;
      x--;
    }
    map[y][x]='#';
    count++;
    guruguru(map,n,x,y,vector,length,count);
    return;
  }
  else if(vector==2){
    y++;
    if(y>=n || map[y+1][x]=='#'){
      vector=4;
      y--;
      x--;
    }
    map[y][x]='#';
    count++;
    guruguru(map,n,x,y,vector,length,count);
    return;
  }
  else if(vector==4){
    x--;
    if(x<0 || map[y][x-1]=='#'){
      vector=8;
      x++;
      y--;
    }
    map[y][x]='#';
    count++;
    guruguru(map,n,x,y,vector,length,count);
    return;
  }
}

int main(void){
  int i,j,k;
  int d,n[N];
  char map[M][M];
  int sx,sy,vector,length,count;

  scanf("%d",&d);
  for(i=0;i<d;i++){
    scanf("%d",&n[i]);
  }
  for(i=0;i<d;i++){
    for(j=0;j<n[i];j++){
      for(k=0;k<n[i];k++){
        map[j][k]=0;
      }
    }
    sx=0;
    sy=n[i]-1;
    vector=8;
    map[sy][sx]='#';
    length=1;
    for(j=2;j<=n[i];j++){
      length+=j;
      if(j%2!=0)
        length++;
    }
    count=0;
    guruguru(map,n[i],sx,sy,vector,length,count);
    for(j=0;j<n[i];j++){
      for(k=0;k<n[i];k++){
        printf("%c",map[j][k]);
      }
      printf("\n");
    }
    if(i<d-1)
      printf("\n");
  }

  return 0;
}

#include <stdio.h>          // printf(), putchar(), scanf()
#include <stdbool.h>

#define MAX_N 100

const char NUL = '\0';

void
spiral(int n)
{
	char field[MAX_N][MAX_N + 1];
	int x, y;
	int s;

	for (y = 0; y < n; ++y)
	{
		for (x = 0; x < n; ++x)
			field[y][x] = ' ';

		field[y][n] = NUL;
	}

	int t = 0, b = n;
	int l = 2, r = n;
	x = 0, y = n;
	while (true)
	{
		if (y <= t)
			break;

		s = y - t;
		for (y--; y >= t; --y)
			field[y][x] = '#';

		y = t;
		t += 2;
		if (s <= 1 || x >= r - 1)
			break;

		s = r - 1 - x;
		for (x++; x < r; ++x)
			field[y][x] = '#';

		x = r - 1;
		r -= 2;
		if (s <= 1 || y >= b - 1)
			break;

		s = b - 1 - y;
		for (y++; y < b; ++y)
			field[y][x] = '#';

		y = b - 1;
		b -= 2;
		if (s <= 1 || x <= l)
			break;

		s = x - l;
		for (x--; x >= l; --x)
			field[y][x] = '#';

		x = l;
		l += 2;
		if (s <= 1)
			break;
	}

	for (y = 0; y < n; ++y)
		printf("%s\n", &field[y][0]);
}

int
main(int argc, char **argv)
{
	int d;
	scanf("%d", &d);
	for (int i = 0; i < d; ++i)
	{
		if (i != 0)
			putchar('\n');

		int n;
		scanf("%d", &n);
		spiral(n);
	}

	return 0;
}

#include <stdio.h>
#include <string.h>

char outFile[128][128];
int patSize; 
int logx, logy;

void makeSpin(int dir, int ty, int tx)
{
	int i, j;
	int dy[] = {-1, 0, 1, 0};
	int dx[] = {0, 1, 0, -1};
	logy = ty;
	logx = tx;
	outFile[ty][tx] = '#';

	for (i = dir; i < dir + 4; i++){
		j = i % 4;
		if (ty + dy[j] > 0 && ty + dy[j] < patSize + 1 && tx + dx[j] > 0 && tx + dx[j] < patSize + 1 && 
				outFile[ty + dy[j]][tx + dx[j]] != '#' && outFile[ty + 2 * dy[j]][tx + 2 * dx[j]] != '#'){
			makeSpin(j, ty + dy[j], tx + dx[j]);
			return;
		}
	}
}	

int main(void)
{
	int testNum;
	int i, j;
	int flag;

	scanf("%d", &testNum);

	flag = 0;
	while (testNum--){
		scanf("%d", &patSize);

		memset(outFile, ' ', sizeof(outFile));

		makeSpin(0, patSize, 1);

		if (patSize % 2 == 0){
			outFile[logy][logx] = ' ';
		}

		if (flag++){
			puts("");
		}

		for (i = 1; i <= patSize; i++){
			for (j = 1; j <= patSize; j++){
				printf("%c", outFile[i][j]);
			}
			puts("");
		}
	}

	return (0);
}

#include <stdio.h>
#include <string.h>

void spiral0(int p[128][128], int y , int x);
void spiral1(int p[128][128], int y, int x);
void spiral2(int p[128][128], int, int);
void spiral3(int p[128][128], int, int);

void spiral0(int p[128][128], int y, int x){
  if(p[y-1][x] + p[y+1][x] + p[y][x-1] + p[y][x+1] <= 1) p[y][x] = 1;
  if(p[y][x - 1] == 0 && p[y][x - 2] == 0) spiral0(p, y, x - 1);
  else if(p[y - 1][x] == 0 && p[y - 2][x] == 0) spiral1(p, y - 1, x);
  else if(p[y][x + 1] == 0 && p[y][x + 2] == 0) spiral2(p, y, x + 1);
  else if(p[y + 1][x] == 0 && p[y + 2][x] == 0) spiral3(p, y + 1, x);
  else return;
}

void spiral1(int p[128][128], int y, int x){
  if(p[y-1][x] + p[y+1][x] + p[y][x-1] + p[y][x+1] <= 1) p[y][x] = 1;
  if(p[y - 1][x] == 0 && p[y - 2][x] == 0) spiral1(p, y - 1, x);
  else if(p[y][x + 1] == 0 && p[y][x + 2] == 0) spiral2(p, y, x + 1);
  else if(p[y + 1][x] == 0 && p[y + 2][x] == 0) spiral3(p, y + 1, x);
  else if(p[y][x - 1] == 0 && p[y][x - 2] == 0) spiral0(p, y, x - 1);
  else return;
}

void spiral2(int p[128][128], int y, int x){
  if(p[y-1][x] + p[y+1][x] + p[y][x-1] + p[y][x+1] <= 1)p[y][x] = 1;
  if(p[y][x + 1] == 0 && p[y][x + 2] == 0) spiral2(p, y, x + 1);
  else if(p[y + 1][x] == 0 && p[y + 2][x] == 0) spiral3(p, y + 1, x);
  else if(p[y][x - 1] == 0 && p[y][x - 2] == 0)spiral0(p, y, x - 1);
  else if(p[y - 1][x] == 0 && p[y - 2][x] == 0) spiral1(p, y - 1, x);
  else return;
}

void spiral3(int p[128][128], int y, int x){
  if(p[y-1][x] + p[y+1][x] + p[y][x-1] + p[y][x+1] <= 1)p[y][x] = 1;
  if(p[y + 1][x] == 0 && p[y + 2][x] == 0) spiral3(p, y + 1, x);
  else if(p[y][x - 1] == 0 && p[y][x - 2] == 0)spiral0(p, y, x - 1);
  else if(p[y - 1][x] == 0 && p[y - 2][x] == 0) spiral1(p, y - 1, x);
  else if(p[y][x + 1] == 0 && p[y][x + 2] == 0) spiral2(p, y, x + 1);
  else return;
}

int main(){
  int p[128][128];
  int d, n, i, j, f = 0;
  scanf("%d", &d);
  while(d--){
    memset(p, 0, sizeof(p));
    scanf("%d", &n);
    for(i = 0; i < n + 4; ++i){
      p[i][0] = 1;
      p[0][i] = 1;
      p[i][n + 3] = 1;
      p[n + 3][i] = 1;
    }
    spiral1(p, n + 1, 2);
    if(f) puts("");
    for(i = 2; i < n + 2; ++i){
      for(j = 2; j < n + 2; ++j) putchar(p[i][j] != 0 ? '#' : ' ');
      puts("");
    }
    f = 1;
  }
  return 0;
}

#include <stdio.h>

enum {
	UP,
	DOWN,
	LEFT,
	RIGHT
};

int main(void) {
	char puttern[100][101];
	int x,y;
	int direction;
	int nomove;
	int putternsize;
	int datamax,datanum;
	scanf("%d",&datamax);
	for(datanum=0;datanum<datamax;datanum++) {
		if(datanum>0)puts("");
		scanf("%d",&putternsize);
		for(y=0;y<putternsize;y++) {
			for(x=0;x<putternsize;x++)puttern[y][x]=' ';
			puttern[y][putternsize]=0;
		}
		x=0;
		y=putternsize-1;
		direction=UP;
		nomove=0;
		while(1) {
			puttern[y][x]='#';
			switch(direction) {
				case UP:
					if(y==0 || (y>1 && puttern[y-2][x]=='#')	
						|| (y>0 && puttern[y-1][x]=='#')	
						|| (y>0 && x>0 && puttern[y-1][x-1]=='#')
						|| (y>0 && x<putternsize-1 && puttern[y-1][x+1]=='#')) {
						direction=RIGHT;
						nomove++;
					} else {
						y--;
						nomove=0;
					}
					break;
				case DOWN:
					if(y==putternsize-1 || (y<putternsize-2 && puttern[y+2][x]=='#')	
						|| (y<putternsize-1 && puttern[y+1][x]=='#')	
						|| (y<putternsize-1 && x>0 && puttern[y+1][x-1]=='#')
						|| (y<putternsize-1 && x<putternsize-1 && puttern[y+1][x+1]=='#')) {
						direction=LEFT;
						nomove++;
					} else {
						y++;
						nomove=0;
					}
					break;
				case LEFT:
					if(x==0 || (x>1 && puttern[y][x-2]=='#')	
						|| (x>0 && puttern[y][x-1]=='#')	
						|| (x>0 && y>0 && puttern[y-1][x-1]=='#')
						|| (x>0 && y<putternsize-1 && puttern[y+1][x-1]=='#')) {
						direction=UP;
						nomove++;
					} else {
						x--;
						nomove=0;
					}
					break;
				case RIGHT:
					if(x==putternsize-1 || (x<putternsize-2 && puttern[y][x+2]=='#')	
						|| (x<putternsize-1 && puttern[y][x+1]=='#')	
						|| (x<putternsize-1 && y>0 && puttern[y-1][x+1]=='#')
						|| (x<putternsize-1 && y<putternsize-1 && puttern[y+1][x+1]=='#')) {
						direction=DOWN;
						nomove++;
					} else {
						y++;
						nomove=0;
					}
					break;
			}
			if(nomove>=4)break;
		}
		for(y=0;y<putternsize;y++)puts(puttern[y]);
	}
	return 0;
}

