#include <stdio.h>

int pattern[256];
char ban[100][100]={0};
int x,y;


int make_pattern(int n)
{
  int last,ret;

  last=(n & 1)?1:2;

  ret=0;
  n--;
  pattern[0]=n;
  pattern[1]=n;
  pattern[2]=n;
  ret = 3;

  while(n > last+2)
  { n -= 2;
    pattern[ret++]=n;
    pattern[ret++]=n;
  }
  pattern[ret++]=last;

  return(ret);
}

void show_pattern(int n,int turn)
{
  int i;

  for(i=0;i<turn;i++)
    printf("%d ",pattern[i]);
  printf("\n");
}

void show_ban(int n)
{
  int i,j;

  for(i=0;i<n;i++)
    {
      for(j=0;j<n;j++)
        printf("%c",ban[i][j]);
      printf("\n");

    }
}

void clear_ban(int n)
{

  int i,j;
  for(i=0;i<n;i++)
    for(j=0;j<n;j++)
       ban[i][j]=' ';
}

void up(){y--;}
void down(){y++;}
void left(){x++;}
void right(){x--;}
void (* move_table[4])()={up,left,down,right};

void draw_uzu(int turn)
{int i,j;

  for(i=0;i<turn;i++)
     for(j=0;j<pattern[i];j++)
       {
         /* printf("%d %d\n",y,x); */
         ban[y][x]='#';
         (*move_table[i % 4])();
       }

}

main()
{
  int cnt,N,turn;
  int flag = 0;

  scanf("%d",&cnt);
  while(cnt--)
  {
    if(flag)
      printf("\n");
    scanf("%d",&N);
    clear_ban(N);
    turn = make_pattern(N);
    x=0;y=N-1;
    draw_uzu(turn);
    show_ban(N);
    flag = 1;
  }
  return(0);
}

