	ArrayList<Integer> Sieve2(int N){
	    ArrayList<Integer> prime = new ArrayList<Integer>();
	    boolean[] list = new boolean[N+1];
	    Arrays.fill(list, true);
	    list[1]=false;
	    for (int i=2; i<=N; i++) {
	        if(list[i]) {
	            prime.add(i);
	            for (int j=i+i; j<=N; j+=i)    list[j] = false;
	        }
	    }
	    return prime;
	}

	boolean[] Sieve(int N){
	    boolean[] list = new boolean[N+1];
	    Arrays.fill(list, true);
	    list[1]=false;
	    for(int i=2; i<=N; i++) {
	        if(list[i]) {
	            for (int j=i+i; j<=N; j+=i)     list[j] = false;
	        }
	    }
	    return list;
	}

	boolean isPrime(int n){
	    for(int i=2; i*i<=n; i++){
	        if(n%i==0)     return false;
	    }
	    return true;
	}

	int gcd(int x, int y){
		if(y==0)	return x;
		else	return gcd(y, x%y);
	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);
		int n = scanner.nextInt();
		while (n-- > 0) {
			int a = scanner.nextInt();
			boolean[][] map = new boolean[a + 4][a + 4];
			for (int i = 0; i < a + 4; i++) {
				for (int j = 0; j < a + 4; j++) {
					if (i == 0 || j == 0 || i == a + 3 || j == a + 3) {
						map[i][j] = true;
					}
				}
			}
			int x = 2;
			int y = a + 1;
			int h = 0;
			int[][] dxy = { { 1, 0 }, { 0, -1 }, { -1, 0 }, { 0, 1 } };
			loop: while (true) {
				for (int i = 0; i < 4; i++) {
					if (i == h) {
						continue;
					}
					int py = y + dxy[i][0];
					int px = x + dxy[i][1];
					if (map[py][px]) {
						break loop;
					}
				}
				map[y][x] = true;
				if (h == 0) {
					if (map[y - 2][x]) {
						x++;
						h = 1;
					} else {
						y--;
					}
				} else if (h == 1) {
					if (map[y][x + 2]) {
						y++;
						h = 2;
					} else {
						x++;
					}
				} else if (h == 2) {
					if (map[y + 2][x]) {
						x--;
						h = 3;
					} else {
						y++;
					}
				} else {
					if (map[y][x - 2]) {
						y--;
						h = 0;
					} else {
						x--;
					}
				}
			}
			for (int i = 2; i < a + 2; i++) {
				for (int j = 2; j < a + 2; j++) {
					System.out.print(map[i][j] ? "#" : " ");
				}
				System.out.println();
			}
			if (n != 0) {
				System.out.println();
			}
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;


public class Main {
	private static  BufferedReader  br = null;

	static {
		br = new BufferedReader(new InputStreamReader(System.in));
	}

	/**
	* @param args
	*/
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cnt = parseCnt();
		for (int i = 0; i < cnt; i++) {
			if (i != 0) {
				System.out.println();
			}
			parseSpiral();
		}
	}

	private static void parseSpiral() {
		int	len = parseCnt();
		if (len > 0) {
			char[][]	map = new char[len][len];

			if (len == 1) {
				map[0][0] = '#';
			} else {
				for (int i = 0, j = len-1; i <= j; i++,j--) {
					if (i%2 == 0) {
						if (i==0) {
							Arrays.fill(map[i], '#');
						} else {
							map[i] = map[j+2].clone();
							map[i][j+1] = ' ';
						}

						if (i != j) {
							map[j] = map[i].clone();
							map[j][i+1] = ' ';
						}
					} else {
						if (i==1) {
							map[i][0] ='#';
							map[i][j+1] ='#';
						} else {
							map[i] = map[j+2].clone();
							map[i][j+1] = '#';
						}

						if (i != j) {
							map[j] = map[i].clone();
							if (i+1 != j) {
								map[j][i+1] = '#';
							}
						}
					}
				}
			}

			for (int i = 0; i < map.length; i++) {
				System.out.println(map[i]);
			}
		}
	}

	private static int parseCnt() {
		int     cnt = 0;
		String  str = null;

		if ((str = parseStdin()) != null) {
			cnt = Integer.parseInt(str);
		}

		return cnt;
	}

	private static String parseStdin() {
		String	stdin = null;

		try {
			String	tmp = br.readLine();
			if (tmp != null) {
				if (!tmp.isEmpty()) stdin = tmp;
				}
			}
		catch (IOException e) {}

		return stdin;
	}
}

import java.io.*;
import java.util.*;

public class Main {
    int cx, cy;
    Scanner sc;

    Main() {
        sc = new Scanner(System.in);
        int d = sc.nextInt();
    }

    int n;
    char dir;
    char[][] map;
    boolean init() {
        if (!sc.hasNext()) return false;
        n = sc.nextInt();
        map = new char[n][n];
        for (int i = 0; i < n; i++) 
            for (int j = 0; j < n; j++)
                map[i][j] = ' ';
        return true;
    }

    boolean first_output = true;

    void run() {
        while (init()) {
            for (int i = n-1; i >= 0 ; i--)  map[i][0] = '#';
            for (int i = 0; i < n ; i++)  map[0][i] = '#';
            for (int i = 0; i < n ; i++)  map[i][n-1] = '#';
            for (int i = n-1; i >= 2 ; i--)  map[n-1][i] = '#';

            if (n <= 4) {
                if (!first_output) 
                    System.out.println("");
                printmap();
                first_output = false;
                continue;
            }

            dir = 'U';
            cx = 2;
            cy = n-1;

fill:
            while (true) {
                switch (dir) {
                    case 'L':
                        if (map[cy][cx-2] == '#') break fill;
                        while (map[cy][cx-2] != '#') {
                            cx--;
                            map[cy][cx] = '#';
                        }
                        if (map[cy][cx+2] != '#') break fill;
                        dir = 'U';
                        break;
                    case 'U':
                        if (map[cy-2][cx] == '#') break fill;
                        while (map[cy-2][cx] != '#') {
                            cy--;
                            map[cy][cx] = '#';
                        }
                        if (map[cy+2][cx] != '#') break fill;
                        dir = 'R';
                        break;
                    case 'R':
                        if (map[cy][cx+2] == '#') break fill;
                        while (map[cy][cx+2] != '#') {
                            cx++;
                            map[cy][cx] = '#';
                        }
                        if (map[cy][cx-2] != '#') break fill;
                        dir = 'D';
                        break;
                    case 'D':
                        if (map[cy+2][cx] == '#') break fill;
                        while (map[cy+2][cx] != '#') {
                            cy++;
                            map[cy][cx] = '#';
                        }
                        if (map[cy-2][cx] != '#') break fill;
                        dir = 'L';
                        break;
                }
            }

            if (!first_output) 
                System.out.println("");
            printmap();
            first_output = false;
        }

    }

    void printmap() {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++)
                System.out.print(map[i][j]);
            System.out.println("");
        }
    }

    public static void main(String[] args) {
        new Main().run();
    }
}

