import java.util.Scanner;

public class Main{
	public static void main(String args[]) {
		Scanner sc = new Scanner(System.in);
		int n;
		int a[][];

		n = sc.nextInt();
		for (int i = 0; i < n ;i++) {
			int k = sc.nextInt();
			a = new int[k][k];
			int x = 0;
			int y = k - 1;
			int dir = 1;
			if (k == 1) {
				a[0][0] = 1;
			} else if (k == 2) {
				a[0][0] = 1;
				a[0][1] = 1;
				a[1][0] = 1;
				a[1][1] = 0;
			} else {
				while (true) {
					a[y][x] = 1;
					if (dir == 1) {
						if (y == 0 || (0 <= y - 2 && a[y - 2][x] == 1)) {
							if (a[y][x + 2] == 1 || a[y + 1][x + 1] == 1) {
								break;
							} else {
								dir = 2;
								x++;
							}
						} else {
							y--;
						}
					} else if (dir == 2) {
						if (x == k - 1 || (x + 2 < k && a[y][x + 2] == 1)) {
							if (a[y + 2][x] == 1 || a[y + 1][x - 1] == 1) {
								break;
							} else {
								dir = 3;
								y++;
							}
						} else {
							x++;
						}
					} else if (dir == 3) {
						if (y == k - 1 || (y + 2 < k && a[y + 2][x] == 1)) {
							if (a[y][x - 2] == 1 || a[y - 1][x - 1] == 1) {
								break;
							} else {
								dir = 4;
								x--;
							}
						} else {
							y++;
						}
					} else {
						if (x == 0 || (0 <= x - 2 && a[y][x - 2] == 1)) {
							if (a[y - 2][x] == 1 || a[y - 1][x + 1] == 1) {
								break;
							} else {
								dir = 1;
								y--;
							}
						} else {
							x--;
						}
					}
				}
			}
			for (int j = 0; j < k; j++) {
				for (int l = 0; l < k; l++) {
					System.out.print((a[j][l] == 1) ? "#" : " ");
				}
				System.out.println();
			}
		}
	}
}

import java.io.*;

public class Main{
	public static void main(String args[]){
		try{
			new Main();
		}catch(IOException e){
			e.printStackTrace();
		}
	}

	public int[][] move = new int[][]{
		{0, -1}, {1, 0}, {0, 1}, {-1, 0}
	};

	public Main() throws IOException{
		BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
		String line = in.readLine();
		int size = Integer.parseInt(line);
		for(int i=0; i<size; i++){
			line = in.readLine();
			int n = Integer.parseInt(line);
			int data[][] = new int[n][n];
			int flag = 1;

			drawCorner(data, flag, 0, 0, n, n);


			for(int y=0; y<n; y++){
				for(int x=0; x<n; x++){
					if(data[y][x]==1){
						System.out.print("#");
					}else{
						System.out.print(" ");
					}
				}
				System.out.println();
			}
		}
	}

	public void drawCorner(int[][] data, int flag, int x, int y, int xm, int ym){
		int first = 1;
		if(x==0 && y==0){
			first = 0;
		}

		if(x>=xm || y>=ym){
			return;
		}

		if(flag==1){
			for(int i=x; i<xm; i++){
				data[y][i] = 1;
			}

			for(int j=y; j<ym; j++){
				data[j][x] = 1;
			}

			drawCorner(data, -flag, x+2, y+1, xm, ym-first);
		}

		if(flag==-1){
			for(int i=x; i<xm; i++){
				data[ym-1][i] = 1;
			}

			for(int j=y; j<ym; j++){
				data[j][xm-1] = 1;
			}

			drawCorner(data, -flag, x, y+1, xm-2, ym-1);
		}
	}
}

