							changed = true;
						}else{
							n_end = false;
						}
					}else{
						n_end = false;
					}

					break;
				case LEFT_MODE:
					if(array[n_y][n_x-2] == ' '){
						n_x--;
						changed = false;
					}else if(changed == false){
						if(array[n_y-1][n_x-1] == ' ' && array[n_y-1][n_x+1] == ' '){
							state = UP_MODE;
							changed = true;
						}else{
							n_end = false;
						}
					}else{
						n_end = false;
					}

					break;	
				case RIGHT_MODE:
					if(array[n_y][n_x+2] == ' '){
						n_x++;
						changed = false;
					}else if(changed == false){
						if(array[n_y+1][n_x-1] == ' ' && array[n_y+1][n_x+1] == ' '){
							state = DOWN_MODE;
							changed = true;
						}else{
							n_end = false;
						}
					}else{
						n_end = false;
					}

					break;
				}
			}


			for (int j = 2; j < (num + 2); j++) {
				for (int k = 2; k < (num + 2); k++) {
					System.out.print(array[j][k]);
				}
				System.out.println();
			}


		}

	}

}

