class FastScanner {
    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int buflen = 0;
    private boolean hasNextByte() {
        if (ptr < buflen) {
            return true;
        }else{
            ptr = 0;
            try {
                buflen = in.read(buffer);
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (buflen <= 0) {
                return false;
            }
        }
        return true;
    }
    private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
    private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
    private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
    public boolean hasNext() { skipUnprintable(); return hasNextByte();}
    public String next() {
        if (!hasNext()) throw new NoSuchElementException();
        StringBuilder sb = new StringBuilder();
        int b = readByte();
        while(isPrintableChar(b)) {
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }
    public long nextLong() {
        if (!hasNext()) throw new NoSuchElementException();
        long n = 0;
        boolean minus = false;
        int b = readByte();
        if (b == '-') {
            minus = true;
            b = readByte();
        }
        if (b < '0' || '9' < b) {
            throw new NumberFormatException();
        }
        while(true){
            if ('0' <= b && b <= '9') {
                n *= 10;
                n += b - '0';
            }else if(b == -1 || !isPrintableChar(b)){
                return minus ? -n : n;
            }else{
                throw new NumberFormatException();
            }
            b = readByte();
        }
    }

    public int nextInt() {
    	 if (!hasNext()) throw new NoSuchElementException();
         int n = 0;
         boolean minus = false;
         int b = readByte();
         if (b == '-') {
             minus = true;
             b = readByte();
         }
         if (b < '0' || '9' < b) {
             throw new NumberFormatException();
         }
         while(true){
             if ('0' <= b && b <= '9') {
                 n *= 10;
                 n += b - '0';
             }else if(b == -1 || !isPrintableChar(b)){
                 return minus ? -n : n;
             }else{
                 throw new NumberFormatException();
             }
             b = readByte();
         }
    }

    public double nextDouble() {
    	return Double.parseDouble(next());
    }


}

import java.util.*;
import java.io.*;

public class Main {

	public void change(int[] d){
		if( d[0] == -1 ){
			d[0] = 0;
			d[1] = 1;
		} else if( d[0] == 0 ){
			if( d[1] == 1 ){
				d[0] = 1;
				d[1] = 0;
			} else {
				d[0] = -1;
				d[1] = 0;
			}
		} else {
			d[0] = 0;
			d[1] = -1;
		}
	}
	public void solve() throws IOException{
		boolean flag = false;
		int n = nextInt();
		for(int i = 0; i < n; i++){
			if( flag ){
				writer.println();
			}
			flag = true;
			int length = nextInt();
			if( length == 1 ){
				writer.println('#');
				continue;
			}
			char[][] ans = new char[length+2][length+2];
			for(int j = 0; j < ans.length; j++){
				for(int k = 0; k < ans.length; k++){
					ans[j][k] = ' ';
				}
			}
			int[] point = new int[2];
			point[0] = length; point[1] = 1;
			int[] dir = new int[2];
			dir[0] = -1; dir[1] = 0;
			int count = 1;
			int repeat = 30;
			while(true){
				if( point[0]+dir[0] > length || point[0]+dir[0] < 1 || point[1]+dir[1] > length || point[1]+dir[1] < 1 ){
					change(dir);
					count = 0;
					if( ans[point[0]+2*dir[0]][point[1]+2*dir[1]] == '#' ){
						ans[point[0]][point[1]] = '#';
						break;
					}
					continue;
				}
				int x = point[0]+dir[0];
				int y = point[1]+dir[1];
				if( ans[x][y] == '#' ){
					if( count < 3 ){
						break;
					}
					point[0] -= dir[0];
					point[1] -= dir[1];
					count = 0;
					change(dir);
					if( ans[point[0]+2*dir[0]][point[1]+2*dir[1]] == '#' ){
						break;
					}
				}
				ans[point[0]][point[1]] = '#';
				point[0] += dir[0];
				point[1] += dir[1];
				count++;
			}
			for(int j = 1; j <= length; j++){
				for(int k = 1; k <= length; k++){
					writer.print(ans[j][k]);
				}
				writer.println();
			}			
		}
		writer.flush();
	}			
	public static void main (String args[]) throws IOException{
		new Main().run();
	}

	BufferedReader reader;
	StringTokenizer tokenizer;
	PrintWriter writer;

	public void run() throws IOException{
		try{
			reader = new BufferedReader(new InputStreamReader(System.in));
			tokenizer = null;
			writer = new PrintWriter(System.out);
			solve();
			reader.close();
			writer.close();
		} catch (Exception e){
			e.printStackTrace();
			System.exit(1);
		}
	}
	public int nextInt() throws IOException{
		return Integer.parseInt(nextToken());
	}		
	public String nextToken() throws IOException{
		while( tokenizer == null || !tokenizer.hasMoreTokens() ){
			tokenizer = new StringTokenizer(reader.readLine());
		}
		return tokenizer.nextToken();
	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);
		int n = scanner.nextInt();
		while (n-- > 0) {
			int a = scanner.nextInt();
			boolean[][] map = new boolean[a + 4][a + 4];
			for (int i = 0; i < a + 4; i++) {
				for (int j = 0; j < a + 4; j++) {
					if (i == 0 || j == 0 || i == a + 3 || j == a + 3) {
						map[i][j] = true;
					}
				}
			}
			int x = 2;
			int y = a + 1;
			int h = 0;
			int[][] dxy = { { 1, 0 }, { 0, -1 }, { -1, 0 }, { 0, 1 } };
			boolean flag = false;
			loop: while (true) {
				if (flag) {
					for (int i = 0; i < 4; i++) {
						if (i == h) {
							continue;
						}
						int py = y + dxy[i][0];
						int px = x + dxy[i][1];
						if (map[py][px]) {
							break loop;
						}
					}
				}
				flag = false;
				map[y][x] = true;
				if (h == 0) {
					if (map[y - 2][x]) {
						x++;
						h = 1;
						flag = true;
					} else {
						y--;
					}
				} else if (h == 1) {
					if (map[y][x + 2]) {
						y++;
						h = 2;
						flag = true;
					} else {
						x++;
					}
				} else if (h == 2) {
					if (map[y + 2][x]) {
						x--;
						h = 3;
						flag = true;
					} else {
						y++;
					}
				} else {
					if (map[y][x - 2]) {
						y--;
						h = 0;
						flag = true;
					} else {
						x--;
					}
				}
			}
			for (int i = 2; i < a + 2; i++) {
				for (int j = 2; j < a + 2; j++) {
					System.out.print(map[i][j] ? "#" : " ");
				}
				System.out.println();
			}
			if (n != 0) {
				System.out.println();
			}
		}
	}
}

